Material Ground(vec3 p)
{
    vec3 aCol = (vec3(.55,0.05,0.15)  + mod(floor(p.y  * 20.),2.)) * sqrt(10. * (p.y + 0.5));
    vec3 dCol = vec3(0.1);
    vec3 sCol = vec3(0);
    float a = 1.;
    return Material(aCol, dCol, sCol, a);
}

Material Sea(vec3 p)
{
    vec3 aCol = vec3(.9,0.4,0.7);
    vec3 dCol = vec3(0.2);
    vec3 sCol = vec3(0);
    float a = 1.;
    return Material(aCol, dCol, sCol, a);
}

Material Bubble(vec3 color) {
  vec3 aCol = color;
  vec3 dCol = 0.6 * vec3(0.9, 0.3, 0.7);
  vec3 sCol = 0.6 * vec3(1., 1., 1.);
  float a = 4.;

  return Material(aCol, dCol, sCol, a);
}

Material Tower(vec3 p, vec3 pos)
{
    float x = random(pos.xz);
    vec3 aCol = vec3(1., 0.6, 0.) + (0.3 * mod(floor(p.y * 12.), 4.) );
    if(x > 0.66) aCol = vec3(1., 0., 1.) + (0.3 * mod(floor(p.y * 12.), 4.) );
    else if(x > 0.33) aCol = vec3(.3, .1, .2) + (0.3 * mod(floor(p.y * 12.), 4.) );

    vec3 dCol = vec3(0.1);
    vec3 sCol = vec3(0.0);
    float a = 2.;

    return Material(aCol, dCol, sCol, a);
}

Surface scene1(vec3 p)
{
    float base = clamp(p.y - 0.15 + 0.6 * noise(p.xz) + 0.12 * pow(length(p), 1.2), -2.0, 2.0);
    Surface co = Surface(1, base, Ground(p));

    // bubbles	
    for( int i=0; i<12; i++ )
    {
        float siz = pow( sin(float(i)*651.74+5.0)*0.5 + 0.5, 0.5 ) / 40.;
        float px = 1.5 * sin(float(i)*321.55+4.1);
        float py = 2. * sin(float(i)*235.54+4.4)*0.5 + mod(0.5*iTime*(0.2+1.2*siz),3.)  - 1. - 2. * siz;
        float pz = clamp(1.5 * sin(float(i)*837.43+6.7), -sqrt(2.25 - px * px), sqrt(2.25 - px * px));

        // bubble size, position and color
        float rad = 0.1 + 0.5*siz;
        vec3  pos = vec3(px, py, pz);
        Surface bubble = Surface(2, sdSphere(p - pos, rad), Bubble(1.2 * mix(vec3(0.2,0.3,0.94), vec3(0.95,0.5,0.1), 0.5+0.5*sin(float(i)*164.2+1.9))));
        co = opUnion(co, bubble);
    }

    // towers

    for( int i=0; i<10; i++ )
    {
        float height = (sin(float(i)*651.74+5.0)*0.15 + 0.55);
        float rad = sin(float(i)*843.13+5.0)*0.5 + 1.;
        float px = 1.5 * sin(float(i)*123.44+7.3);
        float pz = clamp(1.5 * sin(float(i)*731.28+9.7), -sqrt(2.25 - px * px), sqrt(2.25 - px * px));

        vec3 pos = vec3(px, height , pz);
        Surface tower =Surface(4, sdCone(p - pos, vec2(0.2, 0.8), height), Tower(p, pos));
        co = opUnion(co, tower);
    }

    //co = opUnion(co, Surface(2, sdSphere(p, .5), Bubble(vec3(0.))));

    co = opUnion(co, Surface(3, p.y + 0.08 * sin(iTime / 3.0), Sea(p)));
    return co;
}

Surface scene2(vec3 p)
{
    float base = clamp(p.y - 0.15 + 0.6 * noise(p.xz) + 0.12 * pow(length(p), 1.2), -2.0, 2.0);
    Surface co = Surface(1, base, Ground(p));

    // towers

    for( int i=0; i<10; i++ )
    {
        float height = (sin(float(i)*651.74+5.0)*0.15 + 0.55);
        float rad = sin(float(i)*843.13+5.0)*0.5 + 1.;
        float px = 1.5 * sin(float(i)*123.44+7.3);
        float pz = clamp(1.5 * sin(float(i)*731.28+9.7), -sqrt(2.25 - px * px), sqrt(2.25 - px * px));

        vec3 pos = vec3(px, height , pz);
        Surface tower =Surface(4, sdCone(p - pos, vec2(0.2, 0.8), height), Tower(p, pos));
        co = opUnion(co, tower);
    }

    //co = opUnion(co, Surface(2, sdSphere(p, .5), Bubble(vec3(0.))));

    co = opUnion(co, Surface(3, p.y + 0.08 * sin(iTime / 3.0), Sea(p)));
    return co;
}

vec3 calcNormal1(vec3 p)
{
    vec2 e = vec2(1.0, -1.0) * 0.0005;
    return normalize(
      e.xyy * scene1(p + e.xyy).sd +
      e.yyx * scene1(p + e.yyx).sd +
      e.yxy * scene1(p + e.yxy).sd +
      e.xxx * scene1(p + e.xxx).sd);
}

vec3 calcNormal2(vec3 p)
{
    vec2 e = vec2(1.0, -1.0) * 0.0005;
    return normalize(
      e.xyy * scene2(p + e.xyy).sd +
      e.yyx * scene2(p + e.yyx).sd +
      e.yxy * scene2(p + e.yxy).sd +
      e.xxx * scene2(p + e.xxx).sd);
}

Surface rayMarch1(vec3 ro, vec3 rd)
{
    float depth = 0.0;
    Surface co;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++)
    {
        vec3 p = ro + depth * rd;
        co = scene1(p);
        depth += 0.6 * abs(co.sd);
        if (abs(co.sd) < PRECISION || depth > MAX_DIST) break;
    }
    co.sd = depth;
    return co;
}

Surface rayMarch2(vec3 ro, vec3 rd)
{
    float depth = 0.0;
    Surface co;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++)
    {
        vec3 p = ro + depth * rd;
        co = scene2(p);
        depth += 0.6 * abs(co.sd);
        if (abs(co.sd) < PRECISION || depth > MAX_DIST) break;
    }
    co.sd = depth;
    return co;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.);

    vec3 lp = vec3(0.);
    vec3 ro = vec3(1.5 * cos(-iTime / 4.), 0.5 * sin(iTime / 8.) + 1.5, 1.5 * sin(-iTime / 4.));
    vec3 rd = camera(ro, lp) * normalize(vec3(uv, -1));

    Surface co = rayMarch1(ro, rd);
    vec3 p = ro + rd * co.sd;
    vec3 normal = calcNormal1(p);

    vec3 lightPosition = 4. * vec3(cos(iTime / 4.), 5., sin(iTime / 4.));
    vec3 lightDirection = normalize(p - lightPosition);
    float lightIntensity = 0.9;

    col = phong(lightDirection, lightIntensity, normal, rd, co); 

    if (co.id == 2)
    {
        Surface co1 = rayMarch2(ro, rd);
        vec3 p1 = ro + rd * co.sd;
        vec3 normal1 = calcNormal2(p1);
        vec3 refrCol = phong(lightDirection, lightIntensity, normal1, rd, co1);
        col = mix(col, refrCol, 0.6);
    }

    if (co.id == 3)
    {
        vec3 reflRD = reflect(rd, normal);
        vec3 reflRO = p + 1.1 * PRECISION * normal; // move origin a little bit away
        Surface reflCO = rayMarch1(reflRO, reflRD);
        vec3 reflP = reflRO + reflRD * reflCO.sd;
        vec3 reflNormal = calcNormal1(reflP);
        reflCO.sd += co.sd;
        vec3 reflCol = phong(lightDirection, lightIntensity, reflNormal, reflRD, reflCO);
        col = mix(col, reflCol, 0.4);
    }

    col = pow(col, vec3(1.0/2.2));
    fragColor = vec4(col,1.0);
}

const int MAX_MARCHING_STEPS = 40;

const float PRECISION = 1e-3;
const float MAX_DIST = 10.0;

float rand(in float seed, in float minn, in float maxx)
{
    return fract(sin(seed * 614.357) * 865.39) * (maxx - minn) + minn;
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        8.5453123);
}

// noise ref: https://www.shadertoy.com/view/tldSRj
vec2 grad( ivec2 z )  // replace this anything that returns a random vector
{
    // 2D to 1D  (feel free to replace by some other)
    int n = z.x+z.y*1111;

    // Hugo Elias hash (feel free to replace by another one)
    n = (n<<13)^n;
    n = (n*(n*n*15731+789221)+1376312589)>>16;

    n &= 7;
    vec2 gr = vec2(n&1,n>>1)*2.0-1.0;
    return ( n>=6 ) ? vec2(0.0,gr.x) : 
           ( n>=4 ) ? vec2(gr.x,0.0) :
                              gr;                      
}

float noise( in vec2 p )
{
    ivec2 i = ivec2(floor( p ));
     vec2 f =       fract( p );

	vec2 u = f*f*(3.0-2.0*f); // feel free to replace by a quintic smoothstep instead

    return mix( mix( dot( grad( i+ivec2(0,0) ), f-vec2(0.0,0.0) ), 
                     dot( grad( i+ivec2(1,0) ), f-vec2(1.0,0.0) ), u.x),
                mix( dot( grad( i+ivec2(0,1) ), f-vec2(0.0,1.0) ), 
                     dot( grad( i+ivec2(1,1) ), f-vec2(1.0,1.0) ), u.x), u.y);
}
// end ref

float sdSphere(vec3 p, float r )
{
  return length(p) - r;
}

float sdCone( in vec3 p, in vec2 c, float h )
{
    vec2 q = h*vec2(c.x,-c.y)/c.y;
    vec2 w = vec2( length(p.xz), p.y );

	vec2 a = w - q*clamp( dot(w,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = w - q*vec2( clamp( w.x/q.x, 0.0, 1.0 ), 1.0 );
    float k = sign( q.y );
    float d = min(dot( a, a ),dot(b, b));
    float s = max( k*(w.x*q.y-w.y*q.x),k*(w.y-q.y)  );
	return sqrt(d)*sign(s);
}


struct Material {
  vec3 ambientColor;
  vec3 diffuseColor;
  vec3 specularColor;
  float alpha;
};

struct Surface {
  int id;
  float sd;
  Material mat;
};

float smin(float a, float b, float k)
{
	float h = clamp(0.5 + 0.5 *(b - a) / k, 0.0, 1.0);
	return mix(b, a, h) - k *h *(1.0 - h);
}

Surface opUnion(Surface obj1, Surface obj2)
{
    if (obj2.sd < obj1.sd) return obj2;
    return obj1;
}

Surface opUnionS(Surface obj1, Surface obj2)
{
    if(obj1.sd < obj2.sd)
        return Surface(obj1.id, smin(obj1.sd, obj2.sd, 0.1), obj1.mat);
    else
        return Surface(obj2.id, smin(obj1.sd, obj2.sd, 0.1), obj2.mat);
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint)
{
    vec3 cd = normalize(lookAtPoint - cameraPos); // camera direction
    vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
    vec3 cu = normalize(cross(cd, cr)); // camera up
    return mat3(-cr, cu, -cd);
}

vec3 phong(vec3 lightDir, float lightIntensity, vec3 normal, vec3 rd, Surface co)
{
    vec3 bgColor = vec3(0.8, 0.95, 1.);
    if (co.sd > MAX_DIST) return bgColor;

    vec3 ambient = co.mat.ambientColor;

    float dotLN = clamp(dot(-lightDir, normal), 0., 1.);
    vec3 diffuse = co.mat.diffuseColor * dotLN;


    float dotRV = clamp(dot(reflect(lightDir, normal), -rd), 0., 1.);
    vec3 specular = co.mat.specularColor * pow(dotRV, co.mat.alpha);

    vec3 col = lightIntensity * (ambient + diffuse + specular);

    col = mix(col, bgColor, 1.0 - exp(-0.002 * co.sd * co.sd * co.sd));
    return col;

}

//  Function from IÃ±igo Quiles
//  ref: https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}
