// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-07-24 00:04:08

void mainImage(out vec4 O,vec2 F)
{
    O=cos((T F/R))*15.);
}

void mainImage(out vec4 O,vec2 F)
{
    if (iFrame<10) O=vec4(R*.5,h(F.x),0);
    else
    {
        O.rg=T1 F/R).rg+sin(T1 F/R).b*pi+vec2(0,1.5708));
        O.b=T1 F/R).b;
        if (O.x>R.x || O.x<0. || O.y>R.y || O.y<0.)
        {
            O.b=h(iTime*F.x+F.y);
        }
    }
}

void mainImage(out vec4 O,vec2 F)
{
    O-=O;
    for (float n=0.;n<50.;n++)
    {
        O+=T (F+.03*n*sin(n*137.508+vec2(0,1.5708)))/R)*.02;
    }
    O-=.004;
    for (float i;i<agents;i++)
    {
        O+=clamp(5.-length(F-T1 vec2(i,0)/R).xy),0.,1.);
    }
    //O/=agents;
    //O-=.2;
    O=pow(clamp(O,0.,1.),.99+O-O);
}

const float agents = 300.;
#define R iResolution.xy
#define T texture(iChannel0,
#define pi 3.141592653945*2.
#define T1 texture(iChannel1,
//noise from dave_hoskins' sineless hash functions: https://www.shadertoy.com/view/4djSRW
float h(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
