#define PI 3.14159
#define R iResolution

vec3 black=vec3(0.1),white=vec3(.8);
vec3 A=vec3(.1),B=vec3(.3),C=vec3(.5),D=vec3(0, .2, .4);
vec3 palette(float t){return A+B*sin(2.*PI*(C*t+D));}

float flower(vec2 uv, float c) {
    float d=length(uv);
    d=fract(d*3.-iTime);
    return .01/abs(cos(atan(uv.y,uv.x)*c)-d);
}

mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

void mainImage(out vec4 C, in vec2 I) {
    vec2 uv0 = (2.*I-R.xy)/R.y;
    vec3 col=black;
    vec2 uv=uv0;

        uv=uv*rot(sin(-iTime*.5)*cos(.3*iTime));
        float d=length(uv);
        for (float i=1.;i<=4.;i++) {
            uv *= (1./(i));
            float v=flower(uv,i);
            v=pow(v, .4);
            col += mix(col, palette(sqrt(d)-.25*sin(iTime)), v);
        }
        uv=sin(uv);

    C = vec4(1.-col*col,1.0);
}
