// Fork of "wondering light" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-08-14 10:37:53

void mainImage(out vec4 O,vec2 F)
{
    O-=O;
    F-=iResolution.xy*.5;
    O+=dot(normalize(F-vec2(60.+sin(iTime)*10.,0)),sin(vec2(3.1416,4.7124)))/length(F-vec2(60.+sin(iTime)*10.,0))*4.;
    O+=dot(normalize(F+vec2(60.-sin(iTime)*10.,0)),sin(vec2(3.1416,4.7124)))/length(F+vec2(60.-sin(iTime)*10.,0))*4.;
    O*=18.;
    O=pow(O,.5-O+O);
    O*=.5;
    O+=clamp(-abs(abs(F.x)+F.y-100.)+10.,0.,1.)*abs(F.y/iResolution.y);
    O+=max(0.,step(.01,max(0.,-(abs(F.x)-10.-abs(F.y)*.05+5.)*max(0.,sin(F.y*.1-iTime*20.))))*(abs(F.y/iResolution.y)-.2));
}
