void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float the = iTime * 1.57;
    float the2 = the * .5;
    vec2 rc = vec2(0.8 + sin(the2) * 0.4, 0.5 + cos(the2) * 0.4);
    vec2 uv = fragCoord / iResolution.y - rc;
    float sig = atan(uv.y, uv.x);
    float le = 0.1 + 0.1 * abs(sin(the * 0.2));
    float r = 1.0 - (abs(sin(sig + the)) * abs(sin(sig + the + 1.57))) * length(uv) / le;
    fragColor = texture(iChannel0, vec2(0, r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = fragCoord.y / iResolution.y;
    if(r > 0.9) fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    else if(r > 0.5) fragColor = vec4(1.0, r / 0.8 - 0.125, (r - 0.5) / 0.4, 1.0);
    else fragColor = vec4(1.0, r, r * 0.5, 1.0);
}
