void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float d = 4.;
    vec2 uv = fragCoord/iResolution.y;
    uv -= iResolution.xy/iResolution.y/2.;
    vec2 auv = uv;
    vec2 suv = fragCoord/iResolution.xy;
    vec2 t = floor(uv*d);
    float time = iTime +1108.;


    // Time varying pixel color
    uv.x -= sin(uv.y*sin(time*0.1)*10.)*0.1;
    uv.y -= sin(uv.x*sin(time*0.12)*14.)*0.1;
    uv = fract(uv*d)-0.5;
    vec4 col = vec4(vec3(0.5 + 0.5*cos(time+auv.xyx+vec3(0,2,4))), 1.);
    col *= 1.0-abs(0.2-length(uv))*2.;
    vec3 col2 = vec3(sin(length(uv*20.)-time*3.+vec3(0,2,4)));
    col2 *= col.rgb;

    col.a = clamp(col.a, 0., 1.);

    col = vec4(mix(col2.rgb, col.rgb, col.a), 1.);

    // Output to screen
    fragColor = col;
}
