void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 a = (uv - 0.5) * 70.0;
    float f = iTime * 0.5;
    vec4 O = vec4(0.0);
    float x;
    for (float m = 1.0; m < 10.0; m += 3.5) {
        vec2 p = vec2(sin(x = m * 0.07 - f), cos(x));
        float d = length(a * p) - 9.0;
        float combinedDistance = d;
        float dynamic = 3.0 + sin(m * 1.0 + 1.0 * 1.0 - f);
        vec3 color = vec3(sin(m * 0.0), cos(m * 0.5), sin(m * 0.5)) * 1.0 + 1.0;
        O += (0.01 / (abs(combinedDistance) + 0.09)) * dynamic * vec4(color, 1.0);
    }
    O *= 2.7;
    fragColor = O;
}
