float gridLine(vec2 uv, float sc,float thick){
    vec2 grid = fract(uv*sc);
    return 1.-(step(sc*thick,grid.x) * step(sc*thick,grid.y) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;


    /*
    //
    //  ** old version **
    //
    float  PI = 3.141592;
    float  N = 100.0;

    // 
    vec3 lineColor = vec3(1.0,0.0,0.0);


    vec2 Coord = uv * 1000.0;
    Coord = sin(PI/N*Coord);

    // in white bgColor
    //fragColor = vec4(1.0-step(0.995,max(Coord.x,Coord.y))+lineColor,1.0);

    // in black bgColor
    //fragColor = vec4(step(0.995,max(Coord.x,Coord.y))*lineColor,1.0);
    */



    //
    //  ** new version **
    //
    vec3 color = vec3(0.0);
    vec2 grid_uv = uv*250.0;
    color += vec3(0.157,0.938,0.995)*gridLine(grid_uv,0.009,2.0);
    color += vec3(0.006,0.370,0.600)*gridLine(grid_uv,0.09,0.5);
    fragColor = vec4(color,1.0);
}
