vec2 rot(vec2 uv, float r) {
    float sinX = sin (r);
    float cosX = cos (r);
    float sinY = sin (r);
    mat2 rotationMatrix = mat2( cosX, -sinX, sinY, -cosX);
    return uv *rotationMatrix;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 0.05;
    float s = 15.;		//stripes
    float st = 0.3;		//stripe thickness

    vec2 uv = rot(fragCoord/iResolution.xy, -0.2+sin((iTime*speed))*0.05);

    float osc = sin(uv.x*(uv.x-1.5)*9.)*0.2; //was: uv.x+.5)
    uv.y += osc * sin((iTime*speed)+uv.x*5.); //was: +uv.x*2.);
    uv.y = fract(uv.y*s);

    vec3 bg = vec3(.3,.2,.9);
    vec3 fg = vec3(.05,.05,.1);

    float mask = smoothstep(0.4, 1.55, uv.y);
    mask += smoothstep(0.5+st,0.55+st, 1.-uv.y);

    vec3 col = mask*bg + (1.-mask)*fg;

    // Output to screen
    fragColor = vec4(col,1.0);
}
