//https://gist.github.com/EliCDavis/f35a9e4afb8e1c9ae94cce8f3c2c9b9a
int OR(int n1, int n2){

    float v1 = float(n1);
    float v2 = float(n2);

    int byteVal = 1;
    int result = 0;

    for(int i = 0; i < 32; i++){
        bool keepGoing = v1>0.0 || v2 > 0.0;
        if(keepGoing){

            bool addOn = mod(v1, 2.0) > 0.0 || mod(v2, 2.0) > 0.0;

            if(addOn){
                result += byteVal;
            }

            v1 = floor(v1 / 2.0);
            v2 = floor(v2 / 2.0);

            byteVal *= 2;
        } else {
            return result;
        }
    }
    return result;	
}

float MOD(int ix, float y)
{
    float x = float(ix);
    return x - y * floor( x / y );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 col = texture(iChannel0,uv);



    for (int i = 0; i < 2; i++)
    {
        vec3 takrooma = texture(iChannel0,uv+vec2(float(i)/200.0,0.0)).xyz;
    	int r = int(takrooma.x * 255.0);
        int g = int(takrooma.y * 255.0);
        int b = int(takrooma.z * 255.0);

    	int cr = int(col.x * 255.0);
        int cg = int(col.y * 255.0);
        int cb = int(col.z * 255.0);

        col.x = MOD(OR(cr, r),255.0) / 255.0;
        col.y = MOD(OR(cg , g),255.0) / 255.0;
        col.z = MOD(OR(cb , b),255.0) / 255.0;
    }





    // Output to screen
    fragColor = col;
}
