#define r(a) mat2(cos(a+vec4(0,33,11,0))) 
#define L length

void mainImage(out vec4 o, vec2 u) {
    o *= 0.;
    vec3 m, q, p = iResolution;
    u = (u + u - p.xy) / p.y;
    float i, d, b, c, a, e1, e2, T = iTime * .5;
    while(i++ < 1e2)
        p = (.8 + cos(T) * .0) * vec3(u * a, a - .1),
        p.yx *= r(T * .73),
        p.xz *= r(T),
        e1 = cos(T * 6.) * .02 + .02,
        e2 = sin(T * 6.) * .02 + .02,
        m = p - vec3(clamp(p.x, -e1, e1), clamp(p.y, -e2, e2), 0),
        d = clamp(dot(m, m), .001, .003),
        p = cos(p * .4 / d),
        q = vec3(L(p.xy), L(p.yz), L(p.xz)),
        c = min(q.x, min(q.y, q.z)),
        a += d *= min(c - .04, b = 3.6 * L(p) - .4),
        o *= .95 - step(c, b) * q.xyzz,
        o += .04 / exp(4e3 * d - .5);
}
