// Function to draw a cloud using a simple noise function
float cloud(vec2 p) {
    p.x+=iTime/10.;
    if(p.y>.75){
        // Simplex or Perlin noise could be used for better clouds
        //float noise = sin(p.x * 10.0) * cos(p.y * 10.0);
        float noise = sin(p.x*10.+random(p)*audio)*cos(p.y*10.);
        return smoothstep(0.4, 0.5, noise);
    }
    else {
        return 0.;
    }
}

