void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    audio = texture(iChannel0,vec2(sin(iTime),0.)).r/2.;
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    uv.x += 0.5;
    uv.y += 0.5;


    // Draw background gradient
    vec3 backgroundColor = gradientBackground(uv);

    // Draw the sun
    vec2 sunPos = vec2(iResolution.x/4., iResolution.y/4.  );//* sin(iTime)); // Sun moves vertically
    float sunRadius = 0.5 * iResolution.y;
    float sunColor = sun(uv, sunPos, sunRadius);

    // Blend sun color over background
    backgroundColor = mix(backgroundColor, vec3(1.0, 1.0, 0.0), sunColor);

    // Draw clouds
    float cloudDensity = cloud(uv);
    vec3 cloudColor = vec3(1.0); // White color for clouds
    backgroundColor = mix(backgroundColor, cloudColor, cloudDensity);




    // Start with the background color
    vec3 color = backgroundColor;

    // Draw the body with outline
    vec2 uvBody = (uv - bodyCenter) / vec2(bodyWidth, bodyHeight);
    uvBody.y *= 1.34;
    float distBody = length(uvBody);
    if (distBody < 0.5) {
        color = bodyColor;
    } else if (distBody < 0.5 + outlineThickness) {
        color = outlineColor;
    }
    // Draw the face with outline
    float distFace = distance(uv, faceCenter);
    if (distFace < faceRadius) {
        color = faceColor;
    } else if (distFace < faceRadius + outlineThickness) {
        color = outlineColor;
    }
    // Draw the belly with outline
    float distBelly = distance(uv, bellyCenter);
    if (distBelly < bellyRadius) {
        color = bellyColor;
    } else if (distBelly < bellyRadius + outlineThickness) {
        color = outlineColor;
    }
    uv.y-=.0515;
    distBelly = distance(uv, bellyCenter);
    distBelly=distBelly*1.8;
    if (distBelly < bellyRadius) {
        color=vec3(0.5,0.5,0.5);
    } else if (distBelly < bellyRadius + outlineThickness) {
        color = outlineColor;
    }
    uv.y+=.0515;


    uv.y-=.2;
    distBelly = distance(uv, bellyCenter);
    distBelly=distBelly*2.6;
    if (distBelly < bellyRadius) {
        color=vec3(0.5,0.5,0.5);
    } else if (distBelly < bellyRadius + outlineThickness) {
        //color = outlineColor;
    }
    uv.y+=.2;

    // draw the mouth
    uv.y-=.0513;
    vec2 uvMouth = (uv - bodyCenter) / vec2(bodyWidth, bodyHeight);
    uvMouth.y *= 10.34;//+(audio*5.);
    uvMouth.x *=10.;//+(audio*5.);
    uvMouth.y-=0.9;
    uvMouth*=(1.5+audio*2.6);
    float distMouth = length(uvMouth);
    if (distMouth < 0.5) {
        color = bodyColor;
    } else if (distMouth < 0.5 + outlineThickness) {
        color = outlineColor;
        //color = vec3(1.,0.,0.);
    }
    color = mouth(uv, color);
     uv.y+=.0513;

    uv.y-=.405;
    color = mole_boobs(uv, color);
    uv.y+=.405;


    // Draw the nose with outline
    float distNose = distance(uv, nosePosition);
    if (distNose < noseSize) {
        color = noseColor;
    } else if (distNose < noseSize + outlineThickness) {
        color = outlineColor;
    }

    // Draw the eyes with outline
    float distLeftEye = distance(uv, leftEyePosition);
    float distRightEye = distance(uv, rightEyePosition);
    if (distLeftEye < eyeSize) {
        color = eyeColor;
    } else if (distLeftEye < eyeSize + outlineThickness) {
        //color = outlineColor;
    }
    if (distRightEye < eyeSize) {
        color = eyeColor;
    } else if (distRightEye < eyeSize + outlineThickness) {
        //color = outlineColor;
    }

    uv.y+=0.05*(.35+sin(iTime/2.));
    distLeftEye= distance(uv, leftEyePosition);
    distRightEye = distance(uv, rightEyePosition);
    uv.y-=0.05*(.35+sin(iTime/2.));
    // Draw the pupils (no outline for pupils)
    if (distLeftEye < pupilSize) {
        color = pupilColor;
    }
    if (distRightEye < pupilSize) {
        color = pupilColor;
    }

    // Draw the hairs
    float distHair1 = abs(cross(vec3(hairEnd1 - hairStart1, 0.0), vec3(uv - hairStart1, 0.0)).z / length(hairEnd1 - hairStart1));
    if (distHair1 < hairThickness && uv.y > hairStart1.y && uv.y < hairEnd1.y) {
        color = outlineColor;
    }

    float distHair2 = abs(cross(vec3(hairEnd2 - hairStart2, 0.0), vec3(uv - hairStart2, 0.0)).z / length(hairEnd2 - hairStart2));
    if (distHair2 < hairThickness && uv.y > hairStart2.y && uv.y < hairEnd2.y) {
        color = outlineColor;
    }

    float distHair3 = abs(cross(vec3(hairEnd3 - hairStart3, 0.0), vec3(uv - hairStart3, 0.0)).z / length(hairEnd3 - hairStart3));
    if (distHair3 < hairThickness && uv.y > hairStart3.y && uv.y < hairEnd3.y) {
        color = outlineColor;
    }

    // Draw the arms with outline
    float angle = 20.0 * 3.14159265 / 180.0;
    // Compute the rotation matrix components
    float cosTheta = cos(angle);
    float sinTheta = sin(angle);
    // Rotate UV coordinates
    vec2 uv2 = uv; // Translate UV 
    uv2.y-=.185;
    uv2.x+=.051;
    vec2 rotatedUV_LeftArm,rotatedUV_RightArm;
    rotatedUV_LeftArm.x = uv2.x * cosTheta - uv2.y * sinTheta;
    rotatedUV_LeftArm.y = uv2.x * sinTheta + uv2.y * cosTheta;
    //rotatedUV += center; // Translate UV back

    uv2 = uv; // Translate UV 
    uv2.y-=.33;
    uv2.x-=1.51;
    rotatedUV_RightArm.x = uv2.x * -cosTheta - uv2.y * sinTheta;
    rotatedUV_RightArm.y = uv2.x * -sinTheta + uv2.y * cosTheta;

    vec2 uvLeftArm = (rotatedUV_LeftArm - leftArmCenter) / vec2(armWidth, armHeight);
    vec2 uvRightArm = (rotatedUV_RightArm - rightArmCenter) / vec2(armWidth, armHeight);
    uvLeftArm.y *= 1.34;
    uvRightArm.y *= 1.34;
    float distLeftArm = length(uvLeftArm);
    float distRightArm = length(uvRightArm);

    if (distLeftArm < 0.5) {
        color = bodyColor;
    } else if (distLeftArm < 0.5 + outlineThickness) {
        color = outlineColor;
        color = vec3(0.5, 0.5, 0.5);
    }

    if (distRightArm < 0.5) {
        color = bodyColor;
    } else if (distRightArm < 0.5 + outlineThickness) {
        color = outlineColor;
        color = vec3(0.5, 0.5, 0.5);
    }

    color = mole_feet(uv, color);

    // Output the final color
    fragColor = vec4(color, 1.0);
}
