const float waveLen = 5.;
void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
vec2 uv = fragCoord/iResolution.xy;

float distanceToMid = pow(abs(uv.y-0.5)*2., 0.5)/2.;

vec3 camera = vec3(texture(iChannel1, uv));
float mic = float(texture(iChannel0, vec2(uv.x-sin(iTime), 0.)));//just a value

vec3 soundCol = vec3(cos(iTime), sin(iTime), 1.-sin(iTime)+uv.x)*mic*1.5*clamp(1.-distanceToMid, 0., 1.);

fragColor = vec4(soundCol.rgb+camera.rgb, 0.);

}
