void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
            fragColor = vec4(fragCoord / iResolution.y, 1, 0) - vec4(1.0, 0.5, 0.0, 0.0);

    vec3 cameraPosition = vec3(0.0, 0.0, iTime * 0.4);

    for (float i = fragCoord.x + iResolution.x; i >= 0.0; i--)
    {
        float singedDistanceField = length(
           cos(vec3(fragColor.yyy + cameraPosition))
        );

        fragColor *= 0.9 + 0.1 * singedDistanceField;
    }
    fragColor = (fragColor + fragColor.x) * 0.2;
}
