void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;    
    float time = iTime;
    float squareSize = 0.1 * resolution.x;
    vec2 circle1 = vec2(0.5 + 0.3 * cos(time), 0.5 + 0.3 * sin(time)); 
    vec2 circle2 = vec2(0.8 + 0.2 * cos(2.0 * time), 0.8 + 0.2 * sin(2.0 * time)); 
    vec2 circle3 = vec2(0.4 + 0.5 * cos(3.0 * time), 0.5 + 0.1 * sin(3.0 * time));
    vec2 square = vec2(0.3 + 0.1 * cos(3.0 * time), 0.2 + 0.1 * sin(3.0 * time)); 
    vec2 circle4 = vec2(0.7 + 0.1 * cos(3.0 * time), 0.5 + 0.1 * sin(3.0 * time)); 

    vec3 color1 = vec3(circle1.x, circle1.y, 1.); 
    vec3 color2 = vec3(circle2.y, circle2.x, 1.); 
    vec3 color3 = vec3(circle3.x, circle3.y, 2.); 
    vec3 color4 = vec3(0.8, 0.2, 0.1); 
    vec3 color5 = vec3(circle4.y, circle4.y, 0.1); 

    vec3 backgroundColor = 0.2 + 0.1 * cos(180.0 *time + fragCoord.xxx * 0.2 + length(fragCoord.xx) * 0.6);

    float distance1 = length(fragCoord - resolution * circle1);
    float distance2 = length(fragCoord - resolution * circle2);
    float distance3 = length(fragCoord - resolution * circle3);
    float distance4 = length(fragCoord - resolution * square);
    float distance5 = length(fragCoord - resolution * circle4);

    float circleRadius = .1 * resolution.x;

    vec3 finalColor = backgroundColor;

    if (distance1 < circleRadius) {
        finalColor = color1;
    }

    if (distance2 < circleRadius) {
        finalColor = color2;
    }

    if (distance3 < circleRadius) {
        finalColor = color3;
    }

   if (abs(fragCoord.x - resolution.x * square.x) < squareSize && abs(fragCoord.y - resolution.y * square.y) < squareSize) {
        finalColor = color3;
    }

    if (distance5 < circleRadius) {
        finalColor = color5;
    }

    fragColor = vec4(finalColor, 4.0);
}
