/*


*/

// A great read on all this stuff:
// https://graphics.stanford.edu/courses/cs148-10-summer/docs/2006--degreve--reflection_refraction.pdf

// refractive index of some common materials:
// http://hyperphysics.phy-astr.gsu.edu/hbase/Tables/indrf.html
#define REFRACTIVE_INDEX_OUTSIDE 1.00029
#define REFRACTIVE_INDEX_INSIDE  1.445

// lighting parameters
#define LIGHT_DIRECTIONAL_DIR   normalize(-vec3(1.0,2.0,1.0))
#define LIGHT_DIRECTIONAL_COLOR vec3(1.0)
#define LIGHT_AMBIENT           vec3(0.1)
#define MAX_RAYS                15 // 2^(n+1) - 1 where n is the max number of bounces
#define RENDER_DISTANCE         100.0
#define MAX_ITER                100

// object color parameters
#define MAX_SDF_POINT_COUNT 256
#define OBJECT_DIFFUSE      vec3(0.0)
#define OBJECT_SPECPOWER    0.0
#define OBJECT_REFLECTIVITY -1. // How reflective the object is. 1 is fully reflective, 0 is fresnel and -1 is no reflection
#define OBJECT_ABSORB       vec3(8.0, 8.0, 3.0) // for beers law

// SDF type

struct SDF {
    vec4 points[MAX_SDF_POINT_COUNT];
    vec3 color;
    float smoothFactor;
    int len;
};

// hash21 from https://www.shadertoy.com/view/4djSRW
#define HASHSCALE3 vec3(443.897, 441.423, 437.195)
//----------------------------------------------------------------------------------------
//  2 out, 1 in...
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * HASHSCALE3);
	p3 += dot(p3, p3.yzx + 19.19);
	return fract(vec2((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y));
}

//============================================================

float smoothMin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float dPoints(in vec3 rayPos, in SDF inSDF)
{   
    vec4 SDFPoint = inSDF.points[0];
    float d = distance(rayPos,SDFPoint.xyz)-SDFPoint.w;
    for(int i = 1; i < inSDF.len; i++){
        SDFPoint = inSDF.points[i];
        d = smoothMin(d,distance(rayPos,SDFPoint.xyz)-SDFPoint.w,inSDF.smoothFactor);
    }
    return d;
}

vec3 computeNormalSDF(in vec3 point, in SDF inSDF) {
    float e = 0.001;

    float dx = (dPoints(point + vec3(e, 0.0, 0.0), inSDF) - dPoints(point - vec3(e, 0.0, 0.0), inSDF)) / (2.0 * e);
    float dy = (dPoints(point + vec3(0.0, e, 0.0), inSDF) - dPoints(point - vec3(0.0, e, 0.0), inSDF)) / (2.0 * e);
    float dz = (dPoints(point + vec3(0.0, 0.0, e), inSDF) - dPoints(point - vec3(0.0, 0.0, e), inSDF)) / (2.0 * e);

    return normalize(vec3(dx, dy, dz));
}

vec4 RayIntersectSDF(in vec3 rayPos, in vec3 rayDir, in SDF inSDF, in float epsilon)
{
    rayDir = normalize(rayDir);
    float dTraveled = 0.;

    float sdfDistance = dPoints(rayPos, inSDF);
    if( sdfDistance > 0.0){
        for(int i = 0; i < MAX_ITER && dTraveled < RENDER_DISTANCE; i++){
            vec3 currentPos = rayPos + dTraveled * rayDir;
            sdfDistance = dPoints(currentPos, inSDF);
            if(sdfDistance  < epsilon){
                return vec4(dTraveled, computeNormalSDF(currentPos, inSDF));
            }
            dTraveled += sdfDistance ;
        }
        return vec4(-1.);
    }
    else{
        dTraveled += 2. * epsilon;
        for(int i = 0; i < MAX_ITER && dTraveled < RENDER_DISTANCE; i++){
            vec3 currentPos = rayPos + dTraveled * rayDir;
            sdfDistance = -dPoints(currentPos, inSDF);
            if(sdfDistance  < epsilon){
                return vec4(dTraveled, -computeNormalSDF(currentPos, inSDF));
            }
            dTraveled += sdfDistance ;
        }
        return vec4(-1.);
    }
}

//============================================================
// returns t and surface normal
// plane xyz = normal, w = d
// from https://www.cs.princeton.edu/courses/archive/fall00/cs426/lectures/raycast/sld017.htm
vec4 RayIntersectPlane (in vec3 rayPos, in vec3 rayDir, in vec4 plane, out vec2 uv)
{   
    float collisionTime = -(dot(rayPos, plane.xyz)+plane.w) / dot(rayDir, plane.xyz);
    vec3 uaxis = normalize(cross(plane.xyz, vec3(0.0, 1.0, 0.0)));
    vec3 vaxis = normalize(cross(plane.xyz, uaxis));
    vec3 collisionPos = rayPos + rayDir * collisionTime;
    uv.x = dot(collisionPos, uaxis);
    uv.y = dot(collisionPos, vaxis);    
    return vec4(collisionTime, plane.xyz);
}

//============================================================
void TrimRayInterval (vec3 rayPos, vec3 rayDir, inout vec2 rayMinMax, vec4 plane, inout vec3 nearNormal, inout vec3 farNormal)
{
    vec2 uv;
    vec4 rayInfo = RayIntersectPlane(rayPos, rayDir, plane, uv);   
    if (dot(rayInfo.yzw, rayDir) < 0.0)
    {
        if (rayInfo.x > rayMinMax.x)
        {
            rayMinMax.x = rayInfo.x;
            nearNormal = rayInfo.yzw;
        }
    }
    else
    {
        if (rayInfo.x < rayMinMax.y)
        {
            rayMinMax.y = rayInfo.x;
            farNormal = rayInfo.yzw;
        }
    }
}

//============================================================

vec4 RayIntersectObject (in vec3 rayPos, in vec3 rayDir, out int object_identifier)
{
        SDF testSDF;
        testSDF.points[0] = vec4(0.0, 0.0, 0.0, 0.5);
        testSDF.points[1] = vec4(sin(iTime) * 0.85, 0.0, cos(iTime) * 1.7, 0.3);
        testSDF.points[2] = vec4(sin(-iTime) * 1.4, 0.0, cos(-iTime) * 0.7, 0.2);
        testSDF.len = 3;
        testSDF.smoothFactor = 0.4;
        testSDF.color = vec3(0.5,0.5,1.0);

    	return RayIntersectSDF(rayPos,rayDir,testSDF,0.0001);
}

//============================================================
vec3 Checkerboard (vec2 uv)
{
    return vec3(mod(floor(uv.x * 4.0) + floor(uv.y * 4.0), 2.0) < 1.0 ? 1.0 : 0.4);
}

//============================================================
// this is ibox() from https://www.shadertoy.com/view/ld23DV
// Just renamed some things to be more clear and minimized to needed functionality
// returns t and normal
vec4 RayIntersectBase ( in vec3 rayOrigin, in vec3 rayDirection, in vec3 boxHalfSizes, out vec2 uv ) 
{
	// ray-box intersection
    vec3 m = 1.0/rayDirection;    // how many steps on each axis to travel 1 unit on that axis
    vec3 n = m*rayOrigin;         // how many steps on each axis the ray origin is from the object origin
    vec3 k = abs(m)*boxHalfSizes; // how many steps on each axis it takes to get from the center of the box to the edge

    vec3 t1 = -n - k;   // how many steps on each axis to get from the ray origin to the negative surface of the box.
    vec3 t2 = -n + k;   // how many steps on each axis to get from the ray origin to the positive surface of the box.

	float timeNear = max( max( t1.x, t1.y ), t1.z );
	float timeFar = min( min( t2.x, t2.y ), t2.z );

	if( timeNear > timeFar || timeFar < 0.0)
        return vec4(-1.0);

	//vec3 normal = -sign(rayDirection)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);

    if (timeNear < 0.0)
        timeNear = timeFar;

    vec3 relPoint = rayOrigin + rayDirection * timeNear;

    vec3 absRelPoint = abs(relPoint) / boxHalfSizes;

    vec3 normal;
    vec3 uaxis;
    vec3 vaxis;
    // if Y isn't the biggest value it can't be the normal axis
    if (absRelPoint.x >= absRelPoint.y)
    {
        // if X is the winner
        if (absRelPoint.x >= absRelPoint.z)
        {
            normal = vec3(1.0, 0.0, 0.0) * -sign(rayDirection);
			uaxis = vec3(0.0,1.0,0.0);
			vaxis = vec3(0.0,0.0,1.0);            
        }
        // else it's Z
        else
        {
            normal = vec3(0.0, 0.0, 1.0) * -sign(rayDirection);
			uaxis = vec3(1.0,0.0,0.0);
			vaxis = vec3(0.0,1.0,0.0);            
        }
    }
    // else X isn't the biggest value so it can't be the normal axis
    else
    {
        // if Y is the winner
        if (absRelPoint.y >= absRelPoint.z)
        {
            normal = vec3(0.0, 1.0, 0.0) * -sign(rayDirection);
			uaxis = vec3(1.0,0.0,0.0);
			vaxis = vec3(0.0,0.0,1.0);	           
        }
        // else it's Z
        else
        {
            normal = vec3(0.0, 0.0, 1.0) * -sign(rayDirection);
			uaxis = vec3(1.0,0.0,0.0);
			vaxis = vec3(0.0,1.0,0.0);            
        }        
    }

	uv = vec2
	(
		dot(relPoint, uaxis),
		dot(relPoint, vaxis)
	);    

	return vec4( timeNear, normal );    
}

//============================================================
vec3 LightPixel (vec3 rayPos, vec3 rayDir, vec3 diffuse, vec3 normal, float specPower, bool doShadow)
{   
    // ambient
    vec3 pixelColor = diffuse * LIGHT_AMBIENT;
    int ident;

    // see if we are in shadow by casting in the light direction and seeing if we hit the object.
    // if we do hit the object, return just the ambient lighting shaded color we have already calculated.
    if (doShadow && normal.y >= 0.9 && RayIntersectObject(rayPos, -LIGHT_DIRECTIONAL_DIR,ident).x >= 0.0)
        return pixelColor;

    // diffuse
    float dp = clamp(dot(normal, -LIGHT_DIRECTIONAL_DIR), 0.0, 1.0);
    pixelColor += diffuse * dp * LIGHT_DIRECTIONAL_COLOR;

    // specular highlight
    if (specPower > 0.0)
    {
    	vec3 reflection = reflect(-LIGHT_DIRECTIONAL_DIR, normal);
    	dp = clamp(dot(rayDir, reflection), 0.0, 1.0);
		pixelColor += LIGHT_DIRECTIONAL_COLOR * pow(dp, specPower);    
    }

    return pixelColor;
}



//============================================================
vec3 GetSceneRayColor (in vec3 rayPos, in vec3 rayDir)
{
    // Returns the lit RGB for this ray intersecting with the scene, ignoring the main object.
    // Used for reflection off the surface of the object, and refraction out the back of the object.

    // if we hit the box, return the lit box color
    vec2 uv;
    vec4 rayInfo = RayIntersectBase(rayPos + vec3(0.0, 1.51, 0.0), rayDir, vec3(1.0, 1.0, 1.0), uv);
    if (rayInfo.x >= 0.0)
        return LightPixel(rayPos + rayDir*rayInfo.x, rayDir, Checkerboard(uv), rayInfo.yzw, 100.0, true);
    // else return skybox color
    else
        return texture(iChannel0, rayDir).rgb;
}


//============================================================

float fresnel(in vec4 rayInfo, in vec3 rayDir, in float r)
{
    float f = pow(clamp(1. - dot(rayInfo.yzw, -rayDir), 0., 1.), 5.);
    return (0.5 - f)*r*r + 0.5*r + f;

}

vec3 RayTracePixelColor (in vec3 rayPos, in vec3 rayDir)
{   
    vec4 bestRayHitInfo = vec4(1000.0, 0.0, 0.0, 0.0);
    vec3 rayHitDiffuse = vec3(1.0);
    vec3 additiveColor = vec3(0.0);

    vec3 ret = vec3(0.0);

    // see if we've hit the platform and remember if we have
    vec2 uv;    
    vec4 rayInfo = RayIntersectBase(rayPos + vec3(0.0, 1.51, 0.0), rayDir, vec3(1.0, 1.0, 1.0), uv);
    if (rayInfo.x >= 0.0 && rayInfo.x < bestRayHitInfo.x)
    {
        bestRayHitInfo = rayInfo;
        rayHitDiffuse = Checkerboard(uv);
    }


    // if we've hit the main object, and it's closer than the platform
    int ident = 0;
    rayInfo = RayIntersectObject(rayPos, normalize(rayDir),ident);
    if (rayInfo.x >= 0.0 && rayInfo.x < bestRayHitInfo.x)
    {   

        // light the surface of the ball a bit
        additiveColor += LightPixel(rayPos, rayDir, OBJECT_DIFFUSE, rayInfo.yzw, OBJECT_SPECPOWER, false);

        // declarations of all variable used
        int isLeafArr[MAX_RAYS];
        int collisionObject[MAX_RAYS];
        float fresnelArr[MAX_RAYS];

        float rayPosArrX[MAX_RAYS];
        float rayPosArrY[MAX_RAYS];
        float rayPosArrZ[MAX_RAYS];

        float rayDirArrX[MAX_RAYS];
        float rayDirArrY[MAX_RAYS];
        float rayDirArrZ[MAX_RAYS];

        vec3 colorArr[MAX_RAYS];
        int onlyLeaf = (MAX_RAYS-1)/2;
        vec3 reflectDir;
        vec3 reflectPos;
        vec3 refractDir;
        vec3 refractPos;

        // init of the first element of each array
        for(int i = 0; i < MAX_RAYS; i++){
            isLeafArr[i] = 0;
            colorArr[i] = vec3(0.0);
        }
        collisionObject[0] = ident;
        rayPosArrX[0] = rayPos.x;
        rayPosArrY[0] = rayPos.y;
        rayPosArrZ[0] = rayPos.z;

        rayDirArrX[0] = rayDir.x;
        rayDirArrY[0] = rayDir.y;
        rayDirArrZ[0] = rayDir.z;

        // generation of all rays
        for(int i = 0; i < MAX_RAYS; i++){

            // case 1 : the ray does not exist and it has sons
            if(isLeafArr[i] == 2 && i < onlyLeaf){
                isLeafArr[(2*i)+1] = 2;
                isLeafArr[(2*i)+2] = 2;
                continue;
            }

            // the ray exists so we retrieve its data and compute the next intersection
            rayPos = vec3(rayPosArrX[i], rayPosArrY[i], rayPosArrZ[i]);
            rayDir = normalize(vec3(rayDirArrX[i], rayDirArrY[i], rayDirArrZ[i]));
            rayInfo = RayIntersectObject(rayPos, rayDir, ident);
            collisionObject[i] = ident;

            // case 2 : ray intersect nothing or has max bounces
            if(rayInfo.x < 0.0 || i >= onlyLeaf){
                isLeafArr[i] = 1;

                if(i < onlyLeaf){ // if it hasn't reach max bounce we need to marks the next rays in the branch as non-existant
                    isLeafArr[(2*i)+1] = 2;
                    isLeafArr[(2*i)+2] = 2;
                }

                continue;
            }

            // case 3 : ray hasn't reach max bounce and intersect an object
            if(rayInfo.x >= 0.0 && i < onlyLeaf){
                isLeafArr[i] = 0;
                fresnelArr[i] = fresnel(rayInfo, rayDir, OBJECT_REFLECTIVITY);

                int reflectIndex = (2 * i) + 1;
                int refractIndex = (2 * i) + 2;

                // reflection ray
                reflectDir = normalize(reflect(rayDir, rayInfo.yzw));
                reflectPos = rayPos + rayDir * rayInfo.x + 0.001 * reflectDir;

                rayPosArrX[reflectIndex] = reflectPos.x;
                rayPosArrY[reflectIndex] = reflectPos.y;
                rayPosArrZ[reflectIndex] = reflectPos.z;
                rayDirArrX[reflectIndex] = reflectDir.x;
                rayDirArrY[reflectIndex] = reflectDir.y;
                rayDirArrZ[reflectIndex] = reflectDir.z;

                // refraction ray
                vec3 refractDirInside = refract(rayDir, rayInfo.yzw, REFRACTIVE_INDEX_OUTSIDE/REFRACTIVE_INDEX_INSIDE);
                refractPos = rayPos + rayDir * rayInfo.x + refractDirInside * 0.001;
                vec4 rayInfoInside = RayIntersectObject(refractPos, refractDirInside, ident);
                if (rayInfoInside.x >= 0.0) {
                    refractDir = refract(refractDirInside, rayInfoInside.yzw, REFRACTIVE_INDEX_INSIDE/REFRACTIVE_INDEX_OUTSIDE);

                    // in this version of the shader, reflections inside an object are not computed
                    if(refractDir == vec3(0.)){
                        refractDir = refractDirInside;
                    }

                    refractPos += refractDirInside * rayInfoInside.x + 0.001 * refractDir;
                    rayPosArrX[refractIndex] = refractPos.x;
                    rayPosArrY[refractIndex] = refractPos.y;
                    rayPosArrZ[refractIndex] = refractPos.z;
                    rayDirArrX[refractIndex] = refractDir.x;
                    rayDirArrY[refractIndex] = refractDir.y;
                    rayDirArrZ[refractIndex] = refractDir.z;
                }

            }
        }

        // computing the color of the rays (starting from the bottom of the tree)
        for(int i = MAX_RAYS - 1; i >= 0; i--){
            // case 1 : no ray
            if(isLeafArr[i] == 2){
                continue;
            }

            //case 2 : leaf ray
            if(isLeafArr[i] == 1){
                rayPos = vec3(rayPosArrX[i], rayPosArrY[i], rayPosArrZ[i]);
                rayDir = vec3(rayDirArrX[i], rayDirArrY[i], rayDirArrZ[i]);
                colorArr[i] = GetSceneRayColor(rayPos, rayDir);
                continue;
            }

            // case 3 : ray is the combination of a reflect ray and a refract ray
            if(isLeafArr[i] == 0){
                colorArr[i] = fresnelArr[i] * colorArr[(2*i)+1];
                colorArr[i] += (1. - fresnelArr[i]) * colorArr[(2*i)+2];
                continue;
            }
        }

        //return maxReflectionDepth(isLeafArr);
        //return vec3(rayPosArrX[5], rayPosArrY[5], rayPosArrZ[5]);
        return ret + colorArr[0] + additiveColor;

    }
    // else we missed the object, so return either the skybox color, or the platform color, as appropriate
    else
    {
        //return rayDir;

        if (bestRayHitInfo.x == 1000.0)    
            return texture(iChannel0, rayDir).rgb;
        else
        {
            // move the ray to the intersection point (so we can shadow) and light the pixel
        	rayPos += rayDir * bestRayHitInfo.x;    
            return LightPixel(rayPos, rayDir, rayHitDiffuse, bestRayHitInfo.yzw, 100.0, true);    
        }
    }
}

//============================================================
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{        
    //----- Camera Setup
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec3 cameraAt 	= vec3(0.0);

    float angleX = iMouse.z > 0.0 ? 6.28 * mouse.x : 3.14 + iTime * 0.25;
    float angleY = iMouse.z > 0.0 ? (mouse.y * 6.28) - 0.4 : 0.3;
    vec3 cameraPos	= (vec3(sin(angleX)*cos(angleY), sin(angleY), cos(angleX)*cos(angleY))) * 4.0;

    vec3 cameraFwd  = normalize(cameraAt - cameraPos);
    vec3 cameraLeft  = normalize(cross(normalize(cameraAt - cameraPos), vec3(0.0,sign(cos(angleY)),0.0)));
    vec3 cameraUp   = normalize(cross(cameraLeft, cameraFwd));

    float cameraViewWidth	= 6.0;
    float cameraViewHeight	= cameraViewWidth * iResolution.y / iResolution.x;
    float cameraDistance	= 6.0;  // intuitively backwards!

    //----- Ray Setup
    vec2 rawPercent = (fragCoord.xy / iResolution.xy); 
    vec2 percent = rawPercent - vec2(0.5,0.5);

    vec3 rayTarget = (cameraFwd * vec3(cameraDistance,cameraDistance,cameraDistance))
        - (cameraLeft * percent.x * cameraViewWidth)
        + (cameraUp * percent.y * cameraViewHeight);
    vec3 rayDir = normalize(rayTarget);

    //----- Ray Trace
    vec3 finalColor = vec3(0.0);

 	finalColor = RayTracePixelColor(cameraPos, rayDir);

    //----- Final color, gamma corrected
    fragColor = vec4(pow(finalColor, vec3(1.0/2.2)),1.0);        
}
