#define PI 3.14159


vec2 chaos(vec2 uv)
{

    return vec2(cos(uv.x), sin(uv.y)) * 0.2;

}



float line(vec2 p, vec2 a, vec2 b)
{


    vec2 pa = p - a;
    vec2 ba = b - a;

    float t = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * t);

}


void mainImage( out vec4 fragColor, in vec2 u )
{

    vec2 r = iResolution.xy, uv = (u + u - r) / r.y; 

    vec2 uvfr = fract(uv * 1.0 + iTime * 0.4) - 0.5;
    vec2 idr = floor(uv * 1.0 + iTime * 0.4);
    float fl = length(uv);

    // Set this line to 5.0
    float fs = length(uv) * 0.1;
    float f = 0.03 / line(uvfr, chaos(vec2(idr * fs + iTime)), chaos(vec2(idr * fs * 1.0 + iTime)));

    f = pow(f, 2.0) * (1.0 - pow(fl, 2.0));

    fragColor = vec4(vec3(f), 1.0);
}
