vec2 distort(vec2 uv, float time) {
    float repeat = 5.0;
    float speed = 1.0;
    float power = 0.02;
	return uv + vec2(
      sin((uv.x * repeat + time * speed) * 3.14) * power, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = distort(uv, iTime);
	fragColor = vec4(texture(iChannel0, uv) );
}
