void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = (2.*I - iResolution.xy)/iResolution.y, q,
    st = uv, n, p;
    float d, m;
    for(float i = 0.; i < 10.; i++)
    {
        n = N22(vec2(i+1.));
        p = sin(n*iTime);
        q = abs(uv - p) - .5;
        d = length(max(q,0.0)) + min(max(q.x,q.y),0.0);
        //d = sin(d+iTime);
        d = abs(d*10.);
        m += smoothstep(30./iResolution.y, 10./iResolution.y, d);
    }
    O = vec4(vec3(m), 1.0);
}

vec2 N22(vec2 p)
{
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a+= dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}
