vec3 barberPattern(vec3 red, vec3 white, vec3 blue, vec2 uv)
{
    float angledUv = uv.x-uv.y;
    float movingUv = fract(angledUv + (iTime * -0.5));

    float colorMask = step(0.5, movingUv);
    float whiteMask = step(0.5, fract(2.0 * movingUv));

    vec3 colorMap = mix(red,blue,colorMask);
    vec3 totalMap = mix(colorMap, white, whiteMask);

    return totalMap;
}

float verticalMask(vec2 uv)
{
    float verticalGradient = pow(1.0 - (distance(uv.x, 0.5)*5.0),0.5);

    return verticalGradient;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 red = vec3(1,0,0);
    vec3 white = vec3(1,1,1);
    vec3 blue = vec3(0,0,1);

    vec3 totalPattern = barberPattern(red, white, blue, uv) * verticalMask(uv);
    fragColor = vec4(totalPattern,1.0);
}
