// BUFFER A: Raytracing stuff (inspired by Vulkan/Optix rt pipelines)
// COMMON: Helper functions & structs


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

#define PI 3.14159f
#define EPSILON 0.001


#define NUM_SPHERES 3
#define NUM_PLANES 7
#define NUM_LIGHT_SPHERES 3
//-----------------------------------------------------
// Random functions
//-----------------------------------------------------

// we only use 2 sampling strategies
float balanceHeuristic(float pdf, float pdf_other) {
    return pdf / (pdf + pdf_other);
}

float hash1(inout float seed) {
    seed = seed + 0.1f;
    return fract(sin(seed += 0.1)*43758.5453123);
}

vec2 hash2(inout float seed) {
    seed = seed + 0.1f;
    return fract(sin(vec2(seed+=0.1,seed+=0.1))*vec2(43758.5453123,22578.1459123));
}

vec3 hash3(inout float seed) {
    seed = seed + 0.1f;
    return fract(sin(vec3(seed+=0.1,seed+=0.1,seed+=0.1))*vec3(43758.5453123,22578.1459123,19642.3490423));
}


vec2 antiAliasingOffset(inout float seed, vec2 resolution){
    return hash2(seed);
}

vec3 randomCosineDirectionHemisphere(inout float seed){
    vec2 rr = hash2(seed);

    float phi = 2.0*PI*rr.x;
    float x = cos(phi)*sqrt(rr.y);
    float y = sin(phi)*sqrt(rr.y);
    float z = sqrt(1.0-rr.y);

    return vec3(x, y, z);
}

vec3 randomUniformDirectionSphere(inout float seed){
    vec2 rr = hash2(seed);

    float x = cos(2.0f*PI*rr.x) * 2.0f * sqrt(rr.y*(1.0-rr.y));
    float y = sin(2.0f*PI*rr.x) * 2.0f * sqrt(rr.y*(1.0-rr.y));
    float z = 1.0 - 2.0*rr.y;

    return normalize(vec3(x, y, z));
}
//-----------------------------------------------------
// Structs
//-----------------------------------------------------
struct ONB {
    vec3 u, v, w;
};

ONB buildONB(vec3 normal){
    ONB onb;
    onb.w = normal;

    // make sure a is nonparallel to onb.w
    vec3 a = vec3(1.0f, 0.0f, 0.0f);
    if(abs(onb.w.x) > 0.9f){
        a = vec3(0.0f, 1.0f, 0.0f);
    }

    onb.v = normalize(cross(onb.w, a));
    onb.u = cross(onb.w, onb.v);

    return onb;
}

vec3 onbTransform(ONB onb, vec3 vector) {
    return onb.u * vector.x + onb.v * vector.y + onb.w * vector.z;
}

// 0:emissive
// 1: lambertian
// 2: specular
struct Material {
    vec3 emission;
    vec3 albedo;
    int type;
};

vec3 evaluateLambertianBRDF(Material material, vec3 theta_out, vec3 normal, vec3 theta_in){
    return material.albedo/PI;
}

vec3 sampleCosineWeighted(vec3 theta_out, vec3 normal, inout float pdf, inout float seed) {
    ONB onb = buildONB(normal);
    vec3 scatterDirection = onbTransform(onb, randomCosineDirectionHemisphere(seed));

    pdf = max(EPSILON, dot(normalize(scatterDirection), normal))/PI;

    return normalize(scatterDirection);
}

vec3 evaluateSpecularBRFD(Material material, vec3 theta_out, vec3 normal, vec3 theta_in){
     if(dot(normalize(reflect(theta_out, normal)), theta_in) < 1.0f-EPSILON)
        return vec3(0.0f);
    return material.albedo;
}

vec3 sampleSpecular(vec3 theta_out, vec3 normal, inout float pdf, inout float seed) {
    pdf = 1.0f;

    return normalize(reflect(theta_out, normal));
}

bool isEmitter(Material material) {
    return material.type == 0;
}

struct Payload {
    vec3 throughput;
    vec3 accumulatedColor;
    float pdf_bsdf;
    int bounces;
};

struct Ray {
    vec3 origin;
    vec3 direction;
    Payload payload;
};

// intersection geometry
vec3 nSphere( in vec3 pos, in vec4 sph ) {
    return (pos-sph.xyz)/sph.w;
}

float iSphere( in Ray ray, in vec4 sph ) {
    vec3 oc = ray.origin - sph.xyz;
    float b = dot(oc, ray.direction);
    float c = dot(oc, oc) - sph.w * sph.w;
    float h = b * b - c;
    if (h < 0.0) return -1.0;

	float s = sqrt(h);
	float t1 = -b - s;
	float t2 = -b + s;

	return t1 < 0.0 ? t2 : t1;
}

vec3 nPlane( in vec3 ro, in vec4 obj ) {
    return obj.xyz;
}

float iPlane( in Ray ray, in vec4 pla ) {
    return (-pla.w - dot(pla.xyz,ray.origin)) / dot( pla.xyz, ray.direction );
}

struct Camera {
    vec3 origin;
    vec3 forward;
    vec3 up;
    vec3 right;
};

struct HitRecord {
    vec3 point;
    vec3 normal;
    int primitiveID;
    bool didHit;
};

vec3 sampleLight(Material material, HitRecord hit, vec4 lightSpheres[NUM_SPHERES], inout int sampledLightID, inout bool didSample, inout float pdf, inout float seed) {
    // select 1 random light sphere
    vec4 lightSphere;
    sampledLightID = int(floor(hash1(seed)*float(NUM_LIGHT_SPHERES)));
    lightSphere = lightSpheres[sampledLightID];
    pdf = 1.0f/float(NUM_LIGHT_SPHERES);
    // Generate a random point on the light sphere
    vec3 randomUniformDirection = normalize(randomUniformDirectionSphere(seed));
    vec3 ranPointOnSphere = randomUniformDirection * lightSphere.w + lightSphere.xyz;
    vec3 sphereDirection = ranPointOnSphere - hit.point;

    // Check if the sampled point is on the visible side of the surface
    if (dot(sphereDirection, hit.normal) < 0.0f) {
        didSample = false;
        return vec3(0.0f);  // Early exit if the light is not visible from the hit point
    }

    // https://raytracing.github.io/books/RayTracingTheRestOfYourLife.html#cleaninguppdfmanagement/samplingasphereobject
    float distance_to_center = length(lightSphere.xyz - hit.point);
    float cos_theta = sqrt(1.0f - (lightSphere.w * lightSphere.w) / (distance_to_center * distance_to_center));
    float solid_angle = max(EPSILON, 2.0 * PI * (1.0f - cos_theta));
    pdf *= 1.0 / solid_angle;


    return normalize(sphereDirection);
}

float getCosineWeightedSamplingPDF(vec3 normal, vec3 sampledDirection){
    return max(EPSILON, dot(normalize(sampledDirection), normal))/PI;
}

float getSpecularSamplingPDF(vec3 theta_out, vec3 normal, vec3 sampledDirection){
   if(dot(normalize(reflect(theta_out, normal)), sampledDirection) < 1.0f-EPSILON)
        return 0.0f;
    return EPSILON*2.0f;
}

float getLightSamplingPDF(vec3 rayOrigin, vec4 lightSphere, vec3 sampledDirection){
    float distance_to_center = length(lightSphere.xyz - rayOrigin);
    float cos_theta = sqrt(1.0f - (lightSphere.w * lightSphere.w) / (distance_to_center * distance_to_center));
    float solid_angle = max(EPSILON, 2.0 * PI * (1.0f - cos_theta));
    float pdf = 1.0 / solid_angle;
    pdf *= 1.0f/float(NUM_LIGHT_SPHERES);

    return pdf;
}

//https://learnopengl.com/PBR/Theory
float distributionGGX (vec3 N, vec3 H, float roughness){
    float a2    = roughness * roughness * roughness * roughness;
    float NdotH = max (dot (N, H), 0.0);
    float denom = (NdotH * NdotH * (a2 - 1.0) + 1.0);
    return a2 / (PI * denom * denom);
}

vec3 evaluateCookTorranceBRDF(Material material, vec3 theta_out, vec3 normal, vec3 theta_in) {
    vec3 N = normal;
    vec3 V = -theta_out;
    vec3 L = theta_in;
    vec3 H = normalize(V + L);

    vec3 albedo = material.albedo;
    float roughness = 0.05f;
    // Cook-Torrance BRDF

    float NDF = distributionGGX(N, H, roughness);
    float G   = 1.0f;//geometrySmith(N, V, L, roughness);
    float F   = 1.0f;//fresnelSchlick(max(dot(H, V), 0.0), F0);        

    float  numerator   = NDF * G * F;
    float denominator = 4.0 * max(dot(N, V), EPSILON) * max(dot(N, L), EPSILON);
    float  specular    = numerator / max(denominator, 0.001);  

    return specular*material.albedo;
}

vec3 sampleGlossy(vec3 theta_out, vec3 normal, inout float pdf, inout float seed) {
    // Generate two random numbers for sampling
    theta_out = -theta_out;
    vec2 rr = hash2(seed);
    float roughness = 0.05f;
    // GGX sampling in spherical coordinates
    float a = roughness * roughness;
    float theta = atan(sqrt(a * rr.x) / sqrt(1.0 - rr.x));
    float phi = 2.0 * PI * rr.y;

    // Convert spherical coordinates to Cartesian
    vec3 H;
    H.x = sin(theta) * cos(phi);
    H.y = sin(theta) * sin(phi);
    H.z = cos(theta);

    // Transform H to world space if normal is not aligned with Z-axis
    vec3 up = abs(normal.z) < 0.999 ? vec3(0.0, 0.0, 1.0) : vec3(1.0, 0.0, 0.0);
    vec3 tangent = normalize(cross(up, normal));
    vec3 bitangent = cross(normal, tangent);
    H = normalize(tangent * H.x + bitangent * H.y + normal * H.z);

    // Reflect the view vector around the half-vector to get the light direction
    vec3 L = reflect(-theta_out, H);

    // Calculate the PDF for this sample
    float NdotH = max(dot(normal, H), 0.0);
    float VdotH = max(dot(theta_out, H), 0.0);
    float D = distributionGGX(normal, H, roughness);
    pdf = D * NdotH / (4.0 * VdotH + 0.001); // 4 * V Â· H converts the PDF to L-space

    return normalize(L);
}

float glossySamplingPDF(vec3 theta_out, vec3 normal, vec3 theta_in){
    theta_out = -theta_out;
    vec3 H = normalize(theta_out + theta_in);
    float roughness = 0.05f;
    float NdotH = max(dot(normal, H), 0.0);
    float VdotH = max(dot(theta_out, H), 0.0);
    float D = distributionGGX(normal, H, roughness);
    float pdf = D * NdotH / (4.0 * VdotH + 0.001);

    return pdf;

}

vec4 spheres[NUM_SPHERES];
vec4 planes[NUM_PLANES];
Material materials[NUM_SPHERES+NUM_PLANES];
Camera camera;


void initializeMISScene() {
    // Camera
    camera.origin = vec3(0.0f, 2.0f, 0.5f);
    camera.forward = normalize(vec3(0.0f, 0.0f, -1.0f));
    camera.up = normalize(vec3(0.0f, 1.0f, 0.0f));
    camera.right = normalize(vec3(1.0f, 0.0f, 0.0f));

    // Cornell Box
    spheres[0] = vec4(1.5f, 3.3f, -1.9f, 0.2f);
    spheres[1] = vec4(0.0f, 3.3f, -1.9f, 0.4f);
    spheres[2] = vec4(-1.8f, 3.3f, -1.9f, 0.6f);

    planes[0] = vec4( 0.0, 0.0,1.0,4.0);    
    planes[1] = vec4( 0.0, 1.0,0.0,0.0);    
    planes[2] = vec4( 1.0, 0.0,0.0,3.0);
    planes[3] = vec4( -1.0, 0.0,0.0,3.0);
    planes[4] = vec4( 0.0, -1.0, 0.0,4.0);
    planes[5] = vec4( vec3(0.0, 1.0,0.5),-0.4);
    planes[6] = vec4( vec3(0.0, 0.5,1.0),1.7);

    materials[0] = Material(vec3(16.86, 10.76, 8.2), vec3(0.0f), 0);    
    materials[1] = Material(vec3(16.86, 10.76, 8.2)*0.8f, vec3(0.0f), 0);
    materials[2] = Material(vec3(16.86, 10.76, 8.2)*0.5f, vec3(0.0f), 0);

    materials[3] = Material(vec3(0.0f), vec3(.7295, .7355, .729), 1);   
    materials[4] = Material(vec3(0.0f), vec3(.7295, .7355, .729), 1);
    materials[5] = Material(vec3(0.0f), vec3(.117, .4125, .115), 1);
    materials[6] = Material(vec3(0.0f), vec3(.611, .0555, .062), 1);
    materials[7] = Material(vec3(0.0f), vec3(.1, .1, .1),1);
    materials[8] = Material(vec3(0.0f), vec3(.9), 1);
    materials[9] = Material(vec3(0.0f), vec3(.9), 2);
}

Ray raygen(vec2 uv, inout float seed) {
    Ray ray;
   // uv += antiAliasingOffset(seed, iResolution.xy);

    ray.origin = camera.origin;
    ray.direction = normalize(((camera.origin+camera.forward) + camera.right * uv.x + camera.up *uv.y)-camera.origin);

    Payload payload;
    payload.throughput = vec3(1.0f);
    payload.accumulatedColor =vec3(0.0f);
    payload.bounces = 0;
    payload.pdf_bsdf = 1.0f;
    ray.payload = payload;

    return ray;
}

HitRecord intersectScene(Ray ray) {
    HitRecord hit;

    int primitiveID = -1;
    float t = 1e20;
    vec3 normal;

    for(int i = 0; i < NUM_SPHERES; i++){
        float it = iSphere( ray, spheres[i] );
        if( it > EPSILON && it < t ) {
            t = it;
            normal = nSphere(ray.origin + ray.direction * t, spheres[i] ); 
            primitiveID = i;
        }
    }

    for(int i = NUM_SPHERES; i < NUM_SPHERES+NUM_PLANES; i++){
        float it = iPlane(ray, planes[i-NUM_SPHERES]);
        if( it > EPSILON && it < t ) {
            t = it;
            normal = nPlane(ray.origin + ray.direction * t, planes[i-NUM_SPHERES] ); 
            primitiveID = i;
        }
    }

    hit.point = ray.origin + ray.direction * t;
    hit.normal = normalize(normal);
    hit.primitiveID = primitiveID;
    hit.didHit = primitiveID != -1;

    return hit;					  
}

void closestHitBSDF(inout Ray ray, in HitRecord hit, inout float seed){
    Material material = materials[hit.primitiveID];

     // hit light emitter
    if (isEmitter(material)) {
        ray.payload.accumulatedColor += material.emission * ray.payload.throughput;
        // terminate ray
        ray.payload.bounces = 2;
        return;
    }

    vec3 brdf, sampledDirection;
    float pdf_bsdf;
    if(materials[hit.primitiveID].type == 1){
        sampledDirection = sampleCosineWeighted(ray.direction, hit.normal, pdf_bsdf, seed);
        brdf = evaluateLambertianBRDF(material, ray.direction, hit.normal, sampledDirection);
    }
    else {
        sampledDirection = sampleGlossy(ray.direction, hit.normal, pdf_bsdf, seed);
        brdf = evaluateCookTorranceBRDF(material, ray.direction, hit.normal, sampledDirection);
        pdf_bsdf = max(EPSILON, pdf_bsdf);
    }

    float cos_theta = max(0.0f, dot(sampledDirection, hit.normal));

    ray.payload.throughput *= (brdf * cos_theta)/pdf_bsdf;
    // continue ray
    ray.origin = hit.point + hit.normal * EPSILON; 
    ray.direction = sampledDirection;
}

void closestHitDL(inout Ray ray, in HitRecord hit, inout float seed){
    Material material = materials[hit.primitiveID];

     // hit light emitter
    if (isEmitter(material)) {
        ray.payload.accumulatedColor += material.emission * ray.payload.throughput;
        // terminate ray
        ray.payload.bounces = 2;
        return;
    }

    float Visibility = 1.0f;
    float pdf_light, pdf_bsdf;
    int sampledLightID;
    bool didSample = true;
    vec3 sampledDirection = sampleLight(material, hit, spheres, sampledLightID, didSample, pdf_light, seed);
    if(didSample){
        vec3 brdf;
        if(materials[hit.primitiveID].type == 1)
            brdf = evaluateLambertianBRDF(material, ray.direction, hit.normal, sampledDirection);   
        else 
            brdf = evaluateCookTorranceBRDF(material, ray.direction, hit.normal, sampledDirection);

        float cos_theta = max(0.0f, dot(sampledDirection, hit.normal));
        // visibility test
        {
            Ray shadowRay;
            shadowRay.origin = hit.point;
            shadowRay.direction = sampledDirection;
            HitRecord hitShadow = intersectScene(shadowRay);
            if (!hit.didHit || hitShadow.primitiveID != sampledLightID) 
                Visibility = 0.0f;
        }

        pdf_light = max(EPSILON, pdf_light);
        ray.payload.accumulatedColor += Visibility*ray.payload.throughput * brdf * cos_theta *materials[sampledLightID].emission/pdf_light;
    }
    // terminate ray
    ray.payload.bounces = 2;
}


void closestHitMIS(inout Ray ray, in HitRecord hit, inout float seed) {

    Material material = materials[hit.primitiveID];

    // hit light emitter
    if (isEmitter(material)) {
        // now that we hit a light we must account for that with a misWeight
        // because so far we assumed the pdf_light=0.0f;
        float pdf_light = getLightSamplingPDF(ray.origin, spheres[hit.primitiveID], ray.direction);

        // if the ray from the camera directly hits the light, we didn't importance sample the light source
        // => the misWeight should be just 1
        if(ray.payload.bounces == 0)
            pdf_light = 0.0f;
        float misWeight = balanceHeuristic(ray.payload.pdf_bsdf, pdf_light);

        ray.payload.accumulatedColor += material.emission * ray.payload.throughput *misWeight;
        // terminate ray
        ray.payload.bounces  = 2;
        return;
    }

    //    ^
    //    |
    // check the light intersection from BSDF sampling of bounce=0 before terminating
    if(ray.payload.bounces > 0){
        return;
    }    

    ray.payload.pdf_bsdf = 1.0f;

    // light sampling
    float Visibility = 1.0f;
    float pdf_light, pdf_bsdf;
    int sampledLightID;
    bool didSample = true;
    vec3 sampledDirection = sampleLight(material, hit, spheres, sampledLightID, didSample, pdf_light, seed);
    if(didSample){

        vec3 brdf;
        if(materials[hit.primitiveID].type == 1){
            brdf = evaluateLambertianBRDF(material, ray.direction, hit.normal, sampledDirection);
            pdf_bsdf = getCosineWeightedSamplingPDF(hit.normal, -sampledDirection);

        }
        else {
            brdf = evaluateCookTorranceBRDF(material, ray.direction, hit.normal, sampledDirection);
            pdf_bsdf = glossySamplingPDF(ray.direction, hit.normal, sampledDirection);

        }

        float cos_theta = max(0.0f, dot(sampledDirection, hit.normal));
        // visibility test
        {
            Ray shadowRay;
            shadowRay.origin = hit.point;
            shadowRay.direction = sampledDirection;
            HitRecord hitShadow = intersectScene(shadowRay);
            if (!hit.didHit || hitShadow.primitiveID != sampledLightID) {
                Visibility = 0.0f;
            }
        }

        pdf_light = max(EPSILON, pdf_light);
        float misWeight = balanceHeuristic(pdf_light, pdf_bsdf);

        ray.payload.accumulatedColor += Visibility*ray.payload.throughput * brdf * cos_theta * misWeight*materials[sampledLightID].emission/pdf_light;
    }
    // bsdf sampling
    // so far we don't know if we hit a light with that scatter
    // so we assume we don't
    // => pdf_light = 0.0f
    // => misWeight = 1.0f;
    // however we save the pdf_bsdf to the rays payload

    vec3 brdf;

    if(materials[hit.primitiveID].type == 1){
        sampledDirection = sampleCosineWeighted(ray.direction, hit.normal, pdf_bsdf, seed);
        brdf = evaluateLambertianBRDF(material, ray.direction, hit.normal, sampledDirection);
    }
    else {
        sampledDirection = sampleGlossy(ray.direction, hit.normal, pdf_bsdf, seed);
        brdf = evaluateCookTorranceBRDF(material, ray.direction, hit.normal, sampledDirection);
        pdf_bsdf = max(EPSILON, pdf_bsdf);
    }

    float cos_theta = max(0.0f, dot(sampledDirection, hit.normal));
    ray.payload.throughput *= (brdf * cos_theta)/pdf_bsdf;
    ray.payload.pdf_bsdf = pdf_bsdf;
    // continue ray
    ray.origin = hit.point + hit.normal * EPSILON; 
    ray.direction = sampledDirection;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    ////////////////////////////////////////////////////
    // split screen
    // this is probably not the most efficient way
    vec2 uv = (fragCoord*2.0f)/iResolution.xy;// - 1.0f;
    if(uv.y > 1.0f){
        uv =  fract(uv)-vec2(0.5f);
        uv*=2.0f;
        uv.x *= iResolution.x/iResolution.y;
    }
    else {
        uv.x *= iResolution.x/iResolution.y;
        uv -= iResolution.xy/iResolution.yx;
        uv*=2.0f;

        if(abs(uv.x)>2.0f)
            return;
    }
    ////////////////////////////////////////////////////

    float seed = uv.x + uv.y * 3.43121412313 + fract(1.12345314312*iTime);

    // assign sampling type to the different split screen
    int samplingType = 0;
    if(fragCoord.y/iResolution.y < 0.5f)
        samplingType += 2;
    if(fragCoord.x/iResolution.x > 0.5f)
        samplingType += 1;


    initializeMISScene(); 
    Ray ray = raygen(uv, seed);

    while(ray.payload.bounces <= 1 ){

       HitRecord hit = intersectScene(ray);
       if (!hit.didHit)
           break;
       switch(samplingType){
           case 0:
           closestHitBSDF(ray, hit, seed);
           break;
           case 1:
           closestHitDL(ray, hit, seed);
           break;
           default:
           closestHitMIS(ray, hit, seed);
           break;
       }

       ray.payload.bounces += 1;
    }

    vec3 previousColor = texture(iChannel0, fragCoord/iResolution.xy).xyz;
    float ratio = 1.0f/(float(iFrame+1));
    vec3 accumulatedColor = ratio * ray.payload.accumulatedColor + (1.0f-ratio)*previousColor;

    fragColor.xyz = accumulatedColor;
}
