// Shader was shown in Baldi's Basics Randomizer, Edited some stuff to make more cooler

// the glitch amp.
float amp = 0.01;
// the glitch block's offset.
float glitchOffset = 0.0125;
// the glitch block's count.
float glitchBlocks = 20.0;

float texStuff(vec2 uv, int number, float offset)
{
    float offsetREAL = 0.0;
    if (floor(mod(uv.y * glitchBlocks, 2.0)) == 0.0)
    {
        if (floor(mod(uv.y * glitchBlocks, 4.0)) == 0.0)
            offsetREAL = glitchOffset;
        else
            offsetREAL = -glitchOffset;
    }
    return texture(iChannel0, vec2((uv.x + offset) + offsetREAL, uv.y))[number];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col;
    col.r = texStuff(uv, 0, -amp);
    col.g = texStuff(uv, 1, 0.0);
    col.b = texStuff(uv, 2, amp);
    col.a = texStuff(uv, 3, 0.0);
    fragColor = col;
}

