// How time affect the distortion
#define TIME_MULTIPLIER 0.15
// How much strength of the distortion
#define DISTORTION_STRENGTH 0.025

// Distortion direction
#define SCROLL_1 vec2(0.05, 0.05)
#define SCROLL_2 vec2(-0.05, -0.05)


lowp float generateDistortion(sampler2D noise, in float iTime, in vec2 uv)
{
    return texture(noise, uv + iTime * TIME_MULTIPLIER).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Double layer distortion
    float distortion = generateDistortion(iChannel0, iTime, uv + SCROLL_1)
        * generateDistortion(iChannel0, iTime, uv + SCROLL_2);

    // Apply the distortion to uv
    vec2 distortedUv = uv + vec2(distortion * DISTORTION_STRENGTH);

    // Get base texture based on distorted uv
    vec4 color = texture(iChannel1, distortedUv);
    vec4 topColor = smoothstep(0.22, 0.6, distortion) * vec4(1., 1., 1., 1.);

    // Blueish color
    color = mix(color, vec4(0., 0., 0.8, 0.1) + topColor, vec4(0.05));

    fragColor = color;
}
