void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5; 
    uv.x *= iResolution.x / iResolution.y; 

    float time = iTime * 0.5;


    vec3 blue = vec3(0.6, 0.8, 1.0);    
    vec3 yellowGreen = vec3(0.9, 1.0, 0.6); 
    vec3 white = vec3(1.0);                


    float blueMask = smoothstep(0.0, 0.5, uv.y - 0.3 * sin(uv.x * 3.0 + time)); 
    float yellowGreenMask = smoothstep(0.0, 0.5, -uv.y + 0.3 * cos(uv.x * 3.0 - time));

    vec3 color = mix(blue, yellowGreen, yellowGreenMask);
    color = mix(white, color, blueMask); 

    float edge = smoothstep(0.4, 0.6, length(uv));
    color = mix(color, white, edge);


    fragColor = vec4(color, 1.0);
}
