/* Interleaved Gradient Noise by https://www.iryoku.com/ */

float gradientNoise(in vec2 fragCoord)
{
	return fract(52.9829189 * fract(dot(fragCoord, vec2(0.06711056, 0.00583715))));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 cola = vec3(0.1, 0.1, 0.1);
    vec3 colb = vec3(0.05, 0.05, 0.05);

    // Time varying pixel color
    vec3 col = mix(cola, colb, 2.0 * distance(uv, vec2(0.5, 0.5)));

    if (uv.x > 0.5) {
        // Get screen bit depth as a uniform I think?
        float gradientNoise = (1.0/255.0) * gradientNoise(fragCoord);
        col += gradientNoise;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
