float sdQC( in vec2 p )
{
    p = abs(p); if( p.y>p.x ) p=p.yx;

    float a = p.x-p.y;
    float b = p.x+p.y;
    float c = (2.0*b-1.0)/3.0;
    float h = a*a + c*c*c;
    float t;
    if( h>=0.0 )
    {   
        h = sqrt(h);
        t = sign(h-a)*pow(abs(h-a),1.0/3.0) - pow(h+a,1.0/3.0);
    }
    else
    {   
        float z = sqrt(-c);
        float v = acos(a/(c*z))/3.0;
        t = -z*(cos(v)+sin(v)*1.732050808);
    }
    t *= 0.5;
    vec2 w = vec2(-t,t) + 0.75 - t*t - p;
    return length(w) * sign( a*a*0.5+b-1.5 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;

    uv.x*=iResolution.x/iResolution.y;

    vec2 uv0=uv;

    uv.x+=sin(uv.y*8.+iTime)/8.;

    uv.x=round(uv.x*uv.y*3.);

    uv.y+=cos(uv.x*8.+iTime)/8.;

    uv.y=round(uv.y*uv.x*3.);

    uv.x=uv0.x;

    float a=length(uv)*exp(-length(uv0));

    float b=sdQC(uv);

    float c=uv.y;

    a=tan(a*a+iTime)/a;

    a=abs(a);

    b=cos(b*8.+iTime)/8.;

    uv.x+=sin(uv.y*a+iTime*c)/a;

    b=abs(b);

    b=pow((0.01*a)/b,1.2);

    uv.y+=cos(uv.x*b+iTime*c)/a;

    c=sin(c*(uv.x*a)+iTime)/(uv.x*8.);

    c=abs(c);

    c=pow(0.01/c,1.2);

    a=pow(0.01/a,0.55*c);

    vec3 col = vec3(a,b,c);

    fragColor = vec4(col,1.0);
}
