// https://nullprogram.com/blog/2018/07/31/
// https://github.com/skeeto/hash-prospector/issues/19
uint hash(uint x)
{  
#if 0
    x ^= x >> 16;
    x *= 0x21f0aaadU;
    x ^= x >> 15;
    x *= 0xd35a2d97U;
    x ^= x >> 15;
#elif 0
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
#else
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
#endif    
    return x;
}


float random(uint x) {
    return float(hash(x)) / 4294967295.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint tInt = uint(fract((iTime-1.)/3000.)*65536.0)<<16U;
    fragColor = vec4(vec3(random(uint(fragCoord.x) ^ ((uint(fragCoord.y) * 65535U) + tInt))),1.0);
}
