void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; 


    vec3 color = vec3(1.0);

    float thickness = 0.05; 
    float zWidth = 0.8;    
    float zHeight = 0.8;   


    float topLine = step(abs(uv.y - 0.4), thickness) * step(abs(uv.x), zWidth / 2.0);


    float bottomLine = step(abs(uv.y + 0.4), thickness) * step(abs(uv.x), zWidth / 2.0);


    float diagonalLine = step(abs(uv.x - uv.y), thickness) * step(uv.y, 0.4) * step(-uv.y, 0.4);


    float zShape = topLine + bottomLine + diagonalLine;


    color = mix(color, vec3(0.0, 0.0, 0.0), zShape); 


    fragColor = vec4(color, 1.0);
}
