// Flamy Waves By RikR - CC0, No Rights Reserved.

// Basic triangular wave.
float trianglewave(float x) {
    return 4.0 * abs(0.5 - fract(x)) - 1.0;
}

// Basic box shaped wave between -1 and 1.
float boxwave(float x) {
    return sign(fract(0.5 * floor(x)) - 0.25);
}

// Shannon information entropy, periodic version.
float entropy(float p) {
    p = max(0.001, min(0.999, fract(p))); // Fixes glitching and makes it periodic.
    return mix(-log2(p), -log2(1.0 - p), p);
}

// Some curve I came up with by combining the above.
float flamy(float x, float t) {
    float res = boxwave(x + t);
    res = res / entropy(x + t);
    res = res * trianglewave(x);
    return res;
}

// Demo
void mainImage(out vec4 y, in vec2 z) {
    z = 3.0 * (z - 0.5 * iResolution.xy) / iResolution.y; // Normalize UVs.
    y = vec4(flamy(z.x, iTime) > z.y); // Plot by threshold.
}
