float smootherstep_lerp(float from, float to, float x)
{

  // Scale, and clamp x to 0..1 range
  x = clamp(x, 0.0, 1.0);

  // Evaluate polynomial
  float blendK = x * (1.0 + x * (8.0 + x * (-32.0 + x * (40.0 + x * -16.0))));

  return mix(from, to, blendK);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    if (uv.x > 0.5)
    {
        // position in pixels (in low res texture)
        vec2 pixelPositionInLowRes = uv * iChannelResolution[0].xy;

        // fractional part
        vec2 pixelPositionFrac = fract(pixelPositionInLowRes);

        // whole number
        pixelPositionInLowRes = (pixelPositionInLowRes - pixelPositionFrac);

        vec2 pixelSize = 1.0 / iChannelResolution[0].xy;

        vec2 uv0 = pixelPositionInLowRes * pixelSize;
        vec2 uv1 = uv0  + vec2(pixelSize.x, 0.0);
        vec2 uv2 = uv0  + vec2(0.0, pixelSize.y);

        // smoothstep interpolation
        // the result is still filtered but not too blurry 
        float u = smootherstep_lerp(uv0.x, uv1.x, pixelPositionFrac.x);
        float v = smootherstep_lerp(uv0.y, uv2.y, pixelPositionFrac.y);

	    uv = vec2(u,v);
    }

    vec4 col = texture(iChannel0, uv);
    fragColor = vec4(col.rgb,1.0);
}
