// My first complete scene using raymarching to learn and practice procedural rendering techniques.

// The lighthouse is modeled with SDFs after Ar-Men, a notorious open-sea lighthouse in Brittany, France.
// https://en.wikipedia.org/wiki/Ar_Men
// https://www.detienne.net/phares/divers_phare_int_armen_gb.php

// All textures are procedural. The letters painted on the lighthouse (and the title) are composed with 2D SDFs.
// The lightbeam is achieved with volumetric rendering. The waves are an approximation of Gerstner waves: https://www.shadertoy.com/view/XftcDj.

//# define AA

bool SHOWTITLE = true;

const float MATERIAL_SKY = 0.0;
const float MATERIAL_SEA = 1.0;

const float MAX_DIST = 1000.0;

const vec3 lighthouseOrigin = vec3(-40.0, 16.0, 220.0);
const mat2 lighthouseRotation = mat2(0.637423, -0.770513, 0.770513, 0.637423);
const vec3 lighthouseLantern = lighthouseOrigin + vec3(0.0, 24.2, 0.0);


vec3 keylight = normalize( vec3(-1.5, -1.0, -1.5) );



float odd(float v) { return ceil(fract(v * 0.5)); }
float dot2(vec2 v) { return v.x * v.x + v.y * v.y; }


// https://iquilezles.org/articles/distfunctions/
float smin( float a, float b, float k)
{

  float h = max(k - abs(a - b), 0.0) / k;
  return min(a, b) - h * h * h * k * (1.0/6.0);

}


mat2 rotationTriple(float a, float b, float c)
{
    return mat2(a/c, b/c, -b/c, a/c);
}


// https://iquilezles.org/articles/distfunctions/
float opSubtraction( float d1, float d2 )
{
    return max(-d1,d2);
}


// https://iquilezles.org/articles/distfunctions/
float opSmoothSubtraction( float d1, float d2, float k )
{
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}



// PRIMITIVES
// https://iquilezles.org/articles/distfunctions/


float sdOrientedBox( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}


float sdTunnel( in vec2 p, in vec2 wh )
{
    p.x = abs(p.x); p.y = -p.y;
    vec2 q = p - wh;

    float d1 = dot2(vec2(max(q.x,0.0),q.y));
    q.x = (p.y>0.0) ? q.x : length(p)-wh.x;
    float d2 = dot2(vec2(q.x,max(q.y,0.0)));
    float d = sqrt( min(d1,d2) );

    return (max(q.x,q.y)<0.0) ? -d : d;
}


float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}


float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}


float sdBox2D( in vec2 p, in vec2 b )
{
    vec2 d = abs(p) - b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}


float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}


// vertical
float sdCylinder( vec3 p, vec2 h )
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


float sdCylinderInfinite( vec3 p, vec3 c )
{
  return length(p.xz-c.xy)-c.z;
}


float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}


float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}


float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}


// UTILITY FUNCTIONS


vec3 pickClosest( vec3 A, vec3 B )
{

    return (A.x < B.x) ? A : B;

}



vec3 pickFarthest( vec3 A, vec3 B )
{

    return (A.x > B.x) ? A : B;

}


float random(vec2 p) {

  return fract(sin(dot(p, vec2(101.2, 973.0))) * 43758.5453123);

}


float staticNoise(vec2 p) {

  return fract(sin(dot(p, vec2(101.2, 973.0))) * (43758.5453123 + iTime * 8.0));

}


float randomSeed(float i, float s) {

    return fract(fract(sin(i)) * s);

}


vec2 random2D(vec2 i) {

    float d = dot(i, vec2(22.9876, 34.4388));

    return vec2( randomSeed( d, 3141592.65359 ), randomSeed( d, 2951414.95356 ) ) * 2.0 - 1.0;

}


float gradNoise2D(vec2 p) {

    vec2 f = fract(p);

    vec2 fl = mod(floor(p), 20.0);
    vec2 ce = mod(ceil(p), 20.0);

    vec2 a = random2D(vec2(fl.x, fl.y));
    vec2 b = random2D(vec2(ce.x, fl.y));
    vec2 c = random2D(vec2(fl.x, ce.y));
    vec2 d = random2D(vec2(ce.x, ce.y));

    float da = dot(a, f - vec2(0.0, 0.0));
    float db = dot(b, f - vec2(1.0, 0.0));
    float dc = dot(c, f - vec2(0.0, 1.0));
    float dd = dot(d, f - vec2(1.0, 1.0));

    return 0.5 + mix( mix(da, db, f.x), mix(dc, dd, f.x), f.y );

}


float FBM(vec2 p, int i, float d) {

    mat2 r = mat2(4.0/5.0, 3.0/5.0, -3.0/5.0, 4.0/5.0);

    float f = 2.0;
    float w = 0.5;

    float n = gradNoise2D(p) * w;

    for (int j=0; j<i; j++) {

        p = p * r;
        w = w * d;

        n += gradNoise2D(p * f) * w;

        f = f * 2.0;

    }

    return n;

}



float paintedLetters( vec2 p )
{

    float vth = 0.15;
    float hth = vth * 0.85;

    // A
    p = p + vec2(2.35, 0.0);
    vec2 w = vec2(0.8, 1.0);
    float d1 = sdOrientedBox( p - vec2(0.5 * w.x, 1.0), vec2(0.0, -0.47) * w, vec2(0.49, 0.48) * w, hth * 1.02 );
    float d2 = sdOrientedBox( p - vec2(0.5 * w.x, 1.0), vec2(1.0, -0.47) * w, vec2(0.51, 0.48) * w, vth * 0.92 );
    float h1 = sdOrientedBox( p - vec2(0.0, 0.35), vec2(0.65, 0.5) * w, vec2(1.35, 0.5) * w, hth * 0.85 );
    float ap = sdOrientedBox( p - vec2(0.5 * w.x, 1.48), vec2(0.5, -hth * 0.2) * w, vec2(0.5, hth * 0.2) * w, hth );
    float t1 = sdOrientedBox( p - vec2(0.0 * w.x, 0.53), vec2(0.5, -hth * 0.2) * w, vec2(0.5, hth * 0.175) * w, hth );
    float t2 = sdOrientedBox( p - vec2(1.0 * w.x, 0.53), vec2(0.5, -hth * 0.2) * w, vec2(0.5, hth * 0.2) * w, hth );

    float A = min(min(ap, min(t1, t2)), min(h1, min(d1, d2)));

    // R
    p = p + vec2(-1.35, 0.0);
    w = vec2(0.8, 1.0);
    float v1 = sdOrientedBox( p - vec2(vth * 0.5, 0.5), vec2(0.0, 0.0) * w, vec2(0.0, 1.0) * w, vth );
    float a1 = abs(sdTunnel( p.yx - vec2(1.196, 0.6 - vth), vec2(0.24, 0.35))) - hth * 0.5;
    d1 = sdOrientedBox( p - vec2(0.397 + vth * 0.5, 0.955), vec2(0.0, 0.01) * w, vec2(0.45 - hth, -0.414) * w, hth * 1.07 );
    t1 = sdOrientedBox( p - vec2(0.41 * w.x, 0.53), vec2(0.5, -hth * 0.2) * w, vec2(0.5, hth * 0.35) * w, hth * 0.95 );
    float R = min(v1, min(a1, min(d1, t1)));

    // hyphen
    p = p + vec2(-1.1, 0.0);
    w = vec2(0.8, 1.0);
    float hyphen = sdOrientedBox( p - vec2(0.0, 0.95), vec2(-0.4, 0.0) * w, vec2(0.4, 0.0) * w, hth * 0.95 );    

    // M
    p = p + vec2(-0.43, 0.0);
    w = vec2(0.8, 1.0);
    v1 = sdOrientedBox( p - vec2(vth * 0.5, 0.5), vec2(0.0, 0.0) * w, vec2(0.0, 1.0) * w, vth );
    float v2 = sdOrientedBox( p - vec2(1.0 * w.x - vth * 0.5, 0.5), vec2(0.0, 0.0) * w, vec2(0.0, 1.0) * w, vth );

    d1 = sdOrientedBox( p - vec2(vth * 0.6, 1.461), vec2(0.01, 0.0) * w, vec2(0.5 - hth, -0.5) * w, hth * 0.98 );
    d2 = sdOrientedBox( p - vec2(1.0 * w.x - vth * 0.6, 1.461), vec2(-0.5 + hth, -0.5) * w, vec2(-0.01, 0.0) * w, hth * 0.98 );
    ap = sdOrientedBox( p - vec2(0.5 * w.x, 0.99), vec2(0.0, -hth * 0.5) * w, vec2(0.0, hth * 0.5) * w, hth );
    float M = min(v1, min(v2, min(ap, min(d1, d2))));

    // E
    p = p + vec2(-0.97, 0.0);
    w = vec2(0.65, 1.0);
    float v = sdOrientedBox( p - vec2(vth * 0.5, 0.5), vec2(0.0, 0.0) * w, vec2(0.0, 1.0) * w, vth );

    h1 = sdOrientedBox( p - vec2(0.0, hth * 0.5), vec2(0.0, 0.5) * w, vec2(1.0, 0.5) * w, hth );
    float h2 = sdOrientedBox( p - vec2(0.0, 0.5), vec2(0.0, 0.5) * w, vec2(0.8, 0.5) * w, hth );
    float h3 = sdOrientedBox( p - vec2(0.0, 1.0 - hth * 0.5), vec2(0.0, 0.5) * w, vec2(1.0, 0.5) * w, hth );
    float E = min(v, min(h1, min(h2, h3)));

    // N
    p = p + vec2(-0.78, 0.0);
    w = vec2(0.7, 1.0);
    v1 = sdOrientedBox( p - vec2(vth * 0.5, 0.5), vec2(0.0, 0.0) * w, vec2(0.0, 1.0) * w, vth );
    v2 = sdOrientedBox( p - vec2(1.0 * w.x - vth * 0.5, 0.5), vec2(0.0, 0.0) * w, vec2(0.0, 1.0) * w, vth );
    d1 = sdOrientedBox( p - vec2(0.5 * w.x, 1.0), vec2(-0.5 + hth * 1.1, 0.5 - hth * 0.25) * w, vec2(0.5 - hth * 1.1, -0.5 + hth * 0.25) * w, hth * 1.05 );

    float N = min(v1, min(v2, d1));

    return min(A, min(R, min(hyphen, min(M, min(E, N)))));

}


const mat2 mr0 = mat2(0.7071, 0.7071, -0.7071, 0.7071);
const mat2 mr1 = mat2(0, 1, -1, 0);
const mat2 mr2 = mat2(7.0/24.0, 24.0/25.0, -24.0/25.0, 7.0/25.0);
const mat2 mr3 = mat2(48.0/73.0, 55.0/73.0, -55.0/73.0, 48.0/73.0);



vec3 getSkyColor( vec3 rd )
{
    return vec3(0.83, 0.84, 0.86) - rd.y * 0.55 + (rd.x + 0.1) * 0.3;
}


// MATERIAL FUNCTIONS


float brickValueNoise2D(vec2 p, float width, float n) {

    vec2 q = floor(p);

    float xo = odd(q.y);
    q.x = floor(q.x / width - (xo * 0.5));

    vec2 f = fract(q);

    vec2 fl = floor(q);
    vec2 ce = ceil(q);

    float a = random(vec2(fl.x, fl.y));
    float b = random(vec2(ce.x, fl.y));
    float c = random(vec2(fl.x, ce.y));
    float d = random(vec2(ce.x, ce.y));

    //float ns = FBM(p * 2.0, 4, 0.95);

    return (0.5 + mix( mix(a, b, f.x), mix(c, d, f.x), f.y ) * 0.5) * n;

}


float brickMortarLines(vec2 p, float width, float th, float n) 
{    
    p += vec2(th * 0.5);
    vec2 q = floor(p);
    float xo = odd(q.y);    
    q.x = floor((q.x / width - (xo * 0.5)));

    p = p / vec2(width, 1.0) - vec2(xo * 0.5, 0.0);

    float x = 1.0 - step(th/width, min(1.0, (abs(p.x - q.x))));
    float y = 1.0 - step(th, min(1.0, (abs(p.y - q.y))));

    return max(x, y);

}


vec2 brickLuminance(float x, float y, float th, float n) {


  vec2 q = vec2( x, y );
  q += n * 0.1;

  float w = 2.0;
  float n0 = 0.25 + brickValueNoise2D(q, w, n) * 0.65;
  float n1 = brickMortarLines(q, w, (0.5 + n) * th, n);

  return vec2(n0, n1);

}


vec2 brickPattern(vec3 p, vec3 ob)
{

    float lanternOffset = step(2.0, ob.z);
    float towerOffset   = step(3.0, ob.z);

    float isFoundation = 1.0 - lanternOffset;
    float isBrick = step(1.0, ob.z) - step(5.0, ob.z);
    float isLantern = lanternOffset - towerOffset;

    float sx = (1.0 - isLantern * 0.5 + isFoundation
 * 0.06);
    float sy = 1.5 + 2.0 * towerOffset;
    float dy = 0.72 - towerOffset * 0.85 - lanternOffset * 1.52;

    // wrapping bricks around circular base
    float x = atan(p.z, p.x) * 8.9175 * sx;
    float y = p.y * sy - dy;

    // correcting for rectangular dock sides
    x = mix(x, p.x * 1.20 + 0.65, step(6.9,  p.x));
    x = mix(x, p.z * 1.23 + 0.02, step(12.0, p.x));

    float n = FBM( vec2(x, y) * (4.0 + 12.0 * isLantern), 3, 0.95 );

    float a = 0.5 - towerOffset * 0.05 - lanternOffset * 0.25;
    float b = 0.1 + step(2.0, ob.z) * .9;

    float mdepth = 1.0 - towerOffset * 0.5;
    float bdepth = 1.0;

    return brickLuminance(x, y, 0.15 - isLantern * 0.05, n) * isBrick * vec2(bdepth, mdepth);
}



// LIGHTHOUSE SDF

const float MATERIAL_LIGHTHOUSE = 100.0;

const float MATERIAL_LIGHTHOUSE_METAL = 110.0;
const float MATERIAL_LIGHTHOUSE_CONCRETE = 115.0;

const float MATERIAL_LIGHTHOUSE_MASONRY = 120.0;
const float MATERIAL_LIGHTHOUSE_LANTERN_GLASS = 130.0;

const float MATERIAL_LIGHTHOUSE_WINDOW = 150.0;
const float MATERIAL_LIGHTHOUSE_WINDOW_PAINTED = 151.0;
const float MATERIAL_LIGHTHOUSE_WINDOW_CASEMENT = 155.0;

const float MATERIAL_LIGHTHOUSE_DOOR = 160.0;


vec3 lighthousePosition( vec3 p )
{
    vec3 q0 = (p - lighthouseOrigin);
    vec2 q = lighthouseRotation * q0.xz;
    //vec2 q = q0.xz;

    return vec3(q.x, q0.y, q.y);
}


float sdGuardRailBar( vec3 p, float h, float id )
{
    float longbar = float(id == 12.0);

    h = mix(h, 1.8, longbar);
    float th = mix(0.03, 0.035, longbar);

    return sdVerticalCapsule(p, h, th);
}


float sdRailing( vec3 p, float r, float h, float an )
{  
    // circular railing, vertical-axis revolution of a circle
    r = (an > 3.141592*12.0/30.0) ? 0.0 : r;

    vec2 q = vec2(length(p.xz) - r, p.y - h);
    return sdCircle(q, 0.035);  
}


float sdBalconyFrame( vec3 p, float r, float h, mat2 ro )
{

    float hh = h * 0.5;
    float vb1 = sdVerticalCapsule(p, h, 0.035);

    vec3 q = p;
    q.xz = q.zx * ro;
    float vb2 = sdVerticalCapsule(q + vec3(-1.24, 0.0, 0.0), h, 0.035);
    float vb3 = sdVerticalCapsule(q + vec3(-1.24, 0.0, 1.24), h, 0.035);

    float hb11 = sdCapsule( q + vec3(0.0, -hh, 0.0), vec3(0.0, 0.0, 0.0), vec3(1.24, 0.0, 0.0), 0.035 );
    float hb12 = sdCapsule( q + vec3(-1.24, -hh, 0.0), vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, -1.24), 0.035 );

    float hb21 = sdCapsule( q + vec3(0.0, -h, 0.0), vec3(0.0, 0.0, 0.0), vec3(1.24, 0.0, 0.0), 0.035 );
    float hb22 = sdCapsule( q + vec3(-1.24, -h, 0.0), vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, -1.24), 0.035 );

    float vb = min(vb1, min(vb2, vb3));
    float hb = min(min(hb11, hb12), min(hb21, hb22));

    return min( hb, vb ); 

}


float sdGuardRails( vec3 p, float r)
{

    // guardrail height
    float h = 1.4;

     // Circular guardrails
    float rot = -90.0 * 6.283184/360.0;
    vec2 rotv = vec2(cos(rot), sin(rot));
    p.xz = p.xz * mat2(rotv.x, rotv.y, -rotv.y, rotv.x);

    p.x = abs(p.x);
    vec3 q = p;

    float count = 30.0;
    float sp = 3.141592 / count;
    float an = atan(q.z/q.x);

    float id = round(an/sp);
    id = clamp(id, -15.0, 10.0);

    float a1 = sp * (id + 0.0);
    float a2 = sp * (id + 1.0);

    vec2 r1 = vec2(cos(a1), sin(a1));
    vec2 r2 = vec2(cos(a2), sin(a2));

    vec3 q1 = q;
    vec3 q2 = q;

    q1.xz = mat2(r1.x, -r1.y, r1.y, r1.x) * q1.xz;
    q2.xz = mat2(r2.x, -r2.y, r2.y, r2.x) * q2.xz;

    vec3 radOffset = vec3(r, 0.0, 0.0);

    float bars = min( sdGuardRailBar(q1 - radOffset, h, id), sdGuardRailBar(q2 - radOffset, h, id + 1.0) );
    float rail = sdRailing(q, r, h, an);

    // Balcony
    float ab = sp * -12.0;
    vec2 rb = vec2(cos(ab), sin(ab));
    mat2 ro = mat2(rb.x, -rb.y, rb.y, rb.x);

    vec3 qb = q;
    qb.xz = qb.xz * ro;
    float balcony = sdBalconyFrame(qb - radOffset, r, h + 0.6, ro);

    return min( bars, min(rail, balcony) );
}


vec3 beamVector(float i)
{
    // Ar-menâ€™s lantern flashes 3 times every 20 seconds
    float a = (iTime + i) * 6.283184 / 40.0;
    return vec3(cos(a), 0.0, sin(a));
}


float sdLighhouseBeam( vec3 p )
{

    p = p - lighthouseLantern;

    float toLantern = length(p);
    float r = 0.8 + toLantern * 0.02;

    vec3 beamA = beamVector(-0.15);
    float d1 = sdCapsule( p, beamA * -3000.0, beamA * 3000.0, r );

    vec3 beamB = beamVector(0.0);
    float d2 = sdCapsule( p, beamB * -3000.0, beamB * 3000.0, r );

    vec3 beamC = beamVector(0.15);
    float d3 = sdCapsule( p, beamC * -3000.0, beamC * 3000.0, r );

    return min(d1, min(d2, d3));

}


float sdLighthousePole( vec3 p )
{

    float a = sdCylinder(p - vec3(0, -0.35, 0), vec2(0.3 - p.y * 0.05, 0.15)) - 0.01;
    float b = sdCylinder(p - vec3(0, 0.25, 0), vec2(0.15 - p.y * 0.02, 0.5)) - 0.05;

    return smin(a, b, 0.5);

}


float sdGuardRailSegments( vec3 p )
{

    vec3 q = p;
    q.x = abs(q.x);

    float count = 30.0;
    float sp = 6.283184/count;
    float an = atan(q.z/q.x);

    float rid = round(an/sp);
    rid = clamp(rid, -5.0, 10.0);

    float a1 = sp * (rid + 0.0);
    float a2 = sp * (rid + 1.0);
    float a3 = sp * (rid + 0.5);

    vec2 r1 = vec2(cos(a1), sin(a1));
    vec2 r2 = vec2(cos(a2), sin(a2));

    vec3 q1 = q;
    vec3 q2 = q;

    q1.xz = mat2(r1.x, -r1.y, r1.y, r1.x) * q1.xz;
    q2.xz = mat2(r2.x, -r2.y, r2.y, r2.x) * q2.xz;

    float r = 7.8;
    q1 = q1 - vec3(r, 0.0, 0.0);
    q2 = q2 - vec3(r, 0.0, 0.0);

    float verticals = min(sdVerticalCapsule(q1, 1.4, 0.04), sdVerticalCapsule(q2, 1.4, 0.04));

    float id1 = floor(p.y / 0.7);
    id1 = clamp(id1, 1.0, 2.0);
    p.y = p.y - 0.7 * id1 + 1.4;

    float rotA = 2.1;
    p.xz = p.xz * mat2(cos(rotA), sin(rotA), -sin(rotA), cos(rotA));
    an = atan(p.z, p.x);
    r = (an > 3.141592*20.0/30.0) ? 0.0 : r;

    vec2 q3 = vec2(length(p.xz) - r, p.y - 1.45);
    float horizontals = sdCircle(q3, 0.04);

    return min(verticals, horizontals);

}


float sdLadderRungs( vec3 p )
{

    //p.z += 0.5;
    p.xz = vec2(p.z, p.x);
    p.xy = vec2(p.y, p.x);

    float s = 0.4;
    float id = floor((p.x + (s * 0.5)) / s);
    id = clamp(id, -7.0, 6.0);
    p.x = p.x - s * id;
    //p.xz = p.zx;

    return sdVerticalCapsule(p, 1.0, 0.04);

}


float sdLighthouseFoundation2D( vec2 p )
{

    p.xy = vec2(-p.y, -p.x) - 1.0;

    float base = sdCircle(p, 8.0);

    // Dock
    float dock = sdBox2D(p - vec2(0.0, -6.0), vec2(4.0, 6.0));
    base = smin(dock, base, 1.0);

    return base;
}


float sdLighthouseFoundation( vec3 p, float height )
{
    float base = sdCylinder(p, vec2(8.0, height));

    // Dock
    float dock = sdBox(p - vec3(0.0, 0.0, -6.0), vec3(4.0, height, 6.0));
    base = min(dock, base);

    return base;
}

vec3 sdLighthouseBase( vec3 p )
{

    p.xz = vec2(-p.z, -p.x);
    vec3 q = p - vec3(0.0, -14.5, 0.0);

    // Circular base
    float height = 7.6;
    vec3 base = vec3(sdLighthouseFoundation(q, height), MATERIAL_LIGHTHOUSE_MASONRY, float(p.y < -6.9));
    vec3 baseMetal = vec3(0.0, MATERIAL_LIGHTHOUSE_METAL, 0.0);

    // Dock poles
    vec3 qp = q;
    qp.x = qp.x - 0.4;
    float idp = floor(qp.x / 2.0);
    idp = clamp(idp, -2.0, 1.0);
    qp.x = qp.x - (2.0 * idp);
    base.x = min(base.x, sdLighthousePole(qp - vec3(1.0,  height + 0.5, -11.5)));

    vec3 qpr = q - vec3(-2.5, height + 1.1, -11.5);
    qpr.xy = qpr.yx;
    baseMetal.x = sdVerticalCapsule(qpr, 6.0, 0.04);

    vec3 ql = q - vec3(4.0, height * 0.5 + 4.0, -8.6);

    // Dock ladder
    float ladderTrough = sdBox(ql, vec3(0.4, height, 0.55));
    base.x = max(base.x, -ladderTrough);

    float rungs = sdLadderRungs(ql - vec3(-0.1, -height * 0.5, -0.5));
    baseMetal.x = min(baseMetal.x, rungs);

    // Guardrails
    float guardrails = sdGuardRailSegments(q - vec3(0.0, height, 0.0));
    baseMetal.x = min(baseMetal.x, guardrails);

    base.x -= 0.05;

    return pickClosest(base, baseMetal);

}


void sdWindow( vec3 p, inout vec3 tower, float id, float taper )
{

    p.z -= taper - 0.2;

    float r = 3.7;

    vec3 origin = vec3(p.x, p.y, -p.z - r);
    vec3 size = vec3(0.7, 1.0, 1.1);

    // Stone frame
    float windowFrame = max(sdBox(origin, size), sdCylinder(p, vec2(3.85, 20.2)));

    tower = pickClosest( vec3(windowFrame, MATERIAL_LIGHTHOUSE_WINDOW, id), tower );

    size += vec3(-0.3, -0.3, 0.3);
    float windowInset = sdBox(origin, size);
    tower = pickFarthest( vec3(-windowInset, MATERIAL_LIGHTHOUSE_WINDOW, id), tower );

    p.z += taper - id * 0.13 - 0.2;

    float casementOffset = 0.15;
    // Casement: vertical bars
    float sv = 0.45;
    vec3 qv = p - vec3(0.0, -0.72, casementOffset - r);
    float idv = clamp(floor((qv.x + sv * 0.5)/sv), -1.0, 1.0);
    qv.x = qv.x - sv * idv;

    float th = 0.03 + abs(idv) * 0.05;
    float windowCasementVertical = sdVerticalCapsule(qv, 1.44, th);
    tower = pickClosest( vec3(windowCasementVertical, MATERIAL_LIGHTHOUSE_WINDOW_CASEMENT, id), tower);

    // Casement: horizontal bars
    float sh = 0.36;
    vec3 qh = p - vec3(0.45, 0.0, casementOffset - r);
    float idh = clamp(floor((qh.y + sh * 0.5)/sh), -2.0, 2.0);
    qh.y = qh.y - sh * idh;
    qh = vec3(qh.y, -qh.x, qh.z);

    th = 0.03 + smoothstep(1.1, 1.1, abs(idh)) * 0.05;
    float windowCasementHorizontal = sdVerticalCapsule(qh, 0.9, th);
    tower = pickClosest( vec3(windowCasementHorizontal, MATERIAL_LIGHTHOUSE_WINDOW_CASEMENT, id), tower);
}


void sdStairwellWindow( vec3 p, inout vec3 tower, float id, float taper )
{
    p.z -= taper - 0.2;

    float r = 3.7;

    vec3 origin = vec3(p.x, p.y, -p.z - r);
    vec3 size = vec3(0.45, 0.7, 0.7);

    // Stone frame
    float windowFrame = max(sdBox(origin, size), sdCylinder(p, vec2(3.85, 20.2)));

    tower = pickClosest( vec3(windowFrame, MATERIAL_LIGHTHOUSE_WINDOW, id), tower );

    size += vec3(-0.2, -0.2, 0.2);
    float windowInset = sdBox(origin, size);
    tower = pickFarthest( vec3(-windowInset, MATERIAL_LIGHTHOUSE_WINDOW, id), tower );

    float windowCasement = sdBox(origin + vec3(0.0, 0.0, 0.1), vec3(size.x, size.y, 0.1));

    float roundWindow1 = sdCylinderInfinite(vec3(origin.x, origin.z, origin.y), vec3(0.0,  0.19, 0.16));
    float roundWindow2 = sdCylinderInfinite(vec3(origin.x, origin.z, origin.y), vec3(0.0, -0.19, 0.16));

    windowCasement = max(windowCasement, -roundWindow1);
    windowCasement = max(windowCasement, -roundWindow2);

    tower = pickClosest( vec3(windowCasement, MATERIAL_LIGHTHOUSE_WINDOW_CASEMENT, id), tower);
}


vec3 sdLighthouseTower( vec3 p )
{

    float tower = sdCylinder(p, vec2(4.0, 20.2));
    float towerTaper = p.y * 0.02 + exp((p.y + 30.0) * 0.06) * 0.025;
    tower += towerTaper;

    vec3 Tower = vec3(tower, MATERIAL_LIGHTHOUSE_MASONRY, 3.0 + step(5.6, p.y) + step(19.2, p.y));

    // Windows

    float step = 3.7;
    vec3 qw = p;
    qw.y = qw.y - 2.25;
    float idw = clamp(round(qw.y / step), -1.0, 4.0);
    qw.y = qw.y - step * idw;

    sdWindow(qw, Tower, idw, towerTaper);

    float step2 = 3.7;
    vec3 qsw = p;
    qsw.xz = qsw.zx;
    qsw.y = qsw.y - 3.7;
    float idsw = clamp(round(qsw.y / step2), -2.0, 3.0);
    qsw.y = qsw.y - step2 * idsw;

    sdStairwellWindow(qsw, Tower, idsw, towerTaper);

    float towerbase = sdCylinder(p - vec3(0, -6.8, 0), vec2(4.2, 0.25)) - 0.1;
    Tower.x = smin(Tower.x, towerbase, 0.1);

    float taper = sdCylinder(p - vec3(0, 19.2, 0), vec2(3.3 + (p.y - 19.1) * 0.9, 0.3));

    Tower.x = min(Tower.x, taper);

    // Platform

    float platform = sdCylinder(p - vec3(0, 19.8, 0), vec2(3.8, 0.5)) - 0.05;

    vec3 pb = p;
    pb.zx = pb.xz;
    float balcony = sdBox(pb - vec3(0.0, 20.3, 2.4), vec3(1.2, 0.1, 2.5) ) - 0.1;

    Tower.x = min(Tower.x, min(platform, balcony));

    return Tower;

}


float sdWindArrow( vec3 q )
{

    float arrow = sdVerticalCapsule(q.yzx - vec3(0.0, -0.7, 0.0), 1.4, 0.025);
    vec3 qw = q - vec3(0.0, 0.0, -0.6);
    arrow = min(arrow, sdBox(qw, vec3(0.025, 0.05 - clamp(qw.z, -.5, .5) * 0.25, 0.30))); // wing
    vec3 qh = q - vec3(0.0, 0.0,  0.7);
    arrow = min(arrow, sdBox(qh, vec3(0.025, 0.05 - clamp(qh.z, -.5, 0.5) * 0.5, 0.1))); // arrowhead

    return arrow;
}


float sdWindCock( vec3 q )
{
    float windcock = sdVerticalCapsule(q, 3.0, 0.025);

    // cardinal axes
    q.y -= 1.2;
    windcock = min(windcock, sdCapsule(q, vec3(-0.75, 0, 0), vec3(0.75, 0, 0), 0.025));
    windcock = min(windcock, sdCapsule(q, vec3(0, 0, -0.75), vec3(0, 0, 0.75), 0.025));

    // wind arrows
    q.y -= 0.8;
    q.xz = q.xz * lighthouseRotation;
    windcock = min(windcock, sdWindArrow(q));
    q.xz = q.xz * -lighthouseRotation;
    windcock = min(windcock, sdWindArrow(q.zyx));

    return windcock;
}


vec3 sdLanternRoof( vec3 q )
{

    float lanternHalfDome = sdSphere(q, 1.85);
    lanternHalfDome -= pow(abs(sin(5.0 * atan(q.z, q.x) + 1.570796)), 20.0) * 0.07;
    lanternHalfDome = max(-sdBox(q - vec3(0.0, -1.2, 0.0), vec3(2.3, 1.5, 2.3)), lanternHalfDome);

    q.y -= 0.3;
    float lanternRoofRimBase = sdCylinder(q, vec2(1.95, 0.05)) - 0.02; q.y += 0.05;
    float lanternRoofRimEdge = sdCylinder(q, vec2(2.1, 0.01));
    float lanternRoofRim = smin(lanternRoofRimBase, lanternRoofRimEdge, 0.3);

    q.y -= 1.8; float neckRing1 = sdSphere(q, 0.28);
    //q.y -= 0.1; float neckRing2 = sdSphere(q, 0.3);
    q.y -= 0.5; float lanternTop = sdSphere(q, 0.45);
    lanternTop = min(lanternTop, min(neckRing1, 1e20));

    float windcock = sdWindCock(q);

    lanternTop = smin(lanternTop, windcock, 0.3);

    return vec3(smin(smin(lanternHalfDome, lanternTop, 0.2), lanternRoofRim, 0.2), MATERIAL_LIGHTHOUSE_METAL, 0.0);
}


vec3 sdLanternCrane( vec3 p )
{
    vec3 q = p - vec3(-0.5, 0.8, -2.8);

    float mast = sdCylinder(q - vec3(0.0, -0.15, 0.0), vec2(0.12, 2.2));

    float boom = sdVerticalCapsule(q.yzx - vec3(1.95, -2.0, 0.0), 1.8, 0.05);

    vec3 braceAxis = normalize(vec3(0.0, -0.35, 1.0));
    float dbar = sdCapsule(q - vec3(0.0, 1.63, -1.1), braceAxis * -0.95, braceAxis * 0.95, 0.05);
    boom = min(boom, dbar);

    float vbar1 = sdVerticalCapsule(q - vec3(0.0, 1.32, -0.2), 0.62, 0.05);
    boom = min(boom, vbar1);

    float vbar2 = sdVerticalCapsule(q - vec3(0.0, 1.58, -0.85), 0.36, 0.05);
    boom = min(boom, vbar2);

    return vec3(min(mast, boom), MATERIAL_LIGHTHOUSE_METAL, 0.0);
}


vec3 sdLanternWindowFrameSegment( vec3 q, float ido )
{

    q.y -= 4.36;
    vec3 rq = q;

    // rotational repetition
    float an = atan(rq.z, rq.x);
    float sp = 3.141592 / 5.0;
    float id = floor(an / sp);
    float qan0 = (id + ido) * sp;
    float qan1 = ((id + ido) + 0.5) * sp;
    float r = 1.9;

    rq = rq - vec3(cos(qan0) * r, 0.0, sin(qan0) * r);
    rq.xz = rq.xz * mat2(cos(qan1), sin(qan1), -sin(qan1), cos(qan1));

    vec2 panesize = vec2(0.6, 0.5);

    // framing
    float vertical = sdVerticalCapsule(rq - vec3(0.0, -panesize.y, 0.0), panesize.y * 2.0, 0.04 );
    float horizontal1 = sdVerticalCapsule(rq.xzy - vec3(0.0, 0.0, -panesize.y), panesize.x * 2.0, 0.04 );
    float horizontal2 = sdVerticalCapsule(rq.xzy - vec3(0.0, 0.0, panesize.y), panesize.x * 2.0, 0.04 );

    vec2 centerOffset = vec2(-0.0, -panesize.x);
    rq = rq - vec3(0.0, -panesize.y, 0.0);
    rq.xz = rq.xz + centerOffset.xy;
    rq.z = abs(rq.z);

    float diagonal = sdCapsule(rq, vec3(0.0, 0.0, panesize.x-0.15), vec3(0.1, -2.1, 0.1), 0.04);

    return vec3(min(min(vertical, diagonal), min(horizontal1, horizontal2)), MATERIAL_LIGHTHOUSE_METAL, 0.0);

}


vec3 sdLanternWindowGlassSegment( vec3 q, float ido )
{

    q.y -= 4.36;
    vec3 rq = q;

    // rotational repetition
    float an = atan(rq.z, rq.x);
    float sp = 3.141592 / 5.0;
    float id = floor(an / sp);
    float qan0 = (id + ido) * sp;
    float qan1 = ((id + ido) + 0.5) * sp;
    float r = 1.85;

    rq = rq - vec3(cos(qan0) * r, 0.0, sin(qan0) * r);
    rq.xz = rq.xz * mat2(cos(qan1), sin(qan1), -sin(qan1), cos(qan1));

    vec2 panesize = vec2(0.6, 0.5);

    // window panes
    vec3 squarePane = vec3(sdBox(rq - vec3(0.0, 0.0, panesize.x), vec3(0.02, panesize.y, panesize.x)), MATERIAL_LIGHTHOUSE_LANTERN_GLASS, id + 1.0);

    float slope = rq.y * 0.155;
    float triangularPane1 = sdBox(rq - vec3(-0.04 - q.y * 0.05, -2.0, panesize.x), vec3(0.02, 1.5, panesize.x - 0.1 + slope));

    vec3 rqt2 = q - vec3(cos(qan0) * r * 0.95, 0.0, sin(qan0) * r * 0.95);
    rqt2.xz = rqt2.xz * mat2(cos(qan0), sin(qan0), -sin(qan0), cos(qan0));
    rqt2.z += float(panesize);
    float triangularPane2 = sdBox(rqt2 - vec3(0.05 + q.y * 0.01, -2.0, panesize.x), vec3(0.02, 1.5, panesize.x - 0.5 - slope));

    return pickClosest(squarePane, vec3(min(triangularPane1, triangularPane2), MATERIAL_LIGHTHOUSE_LANTERN_GLASS, id));

}


vec3 sdLantern( vec3 p )
{

    vec3 q = p - vec3(0.0, 21.6, 0.0);

    vec3 lanternFoundation = vec3(sdCylinder(q, vec2(2.4, 1.5)), MATERIAL_LIGHTHOUSE_MASONRY, 2.0);
    float lanternFoundationRim = sdCylinder(q - vec3(0, 1.45, 0), vec2(2.6, 0.15)) - 0.04;
    lanternFoundation.x = min(lanternFoundation.x, lanternFoundationRim);

    vec3 lanternBase = vec3(sdCylinder(q - vec3(0, 1.7, 0), vec2(1.95, 0.15)) - 0.02, MATERIAL_LIGHTHOUSE_METAL, 0.0);
    vec3 lantern0 = sdLanternWindowFrameSegment(q, 0.0);
    vec3 lantern1 = sdLanternWindowFrameSegment(q, 1.0);
    vec3 lantern = pickClosest(pickClosest(lantern0, lantern1), lanternBase);

    lantern = pickClosest(lantern, sdLanternCrane(q));

    q.y -= 4.7;
    vec3 lanternRoof = sdLanternRoof(q);

    return pickClosest(pickClosest(lantern, lanternFoundation), lanternRoof);
}


vec3 sdLanternGlass( vec3 p )
{

    vec3 q = lighthousePosition(p);
    q = q - vec3(0.0, 21.6, 0.0);

    vec3 lantern0 = sdLanternWindowGlassSegment(q, 0.0);
    vec3 lantern1 = sdLanternWindowGlassSegment(q, 1.0);
    return pickClosest(lantern0, lantern1);
}



vec3 sdLighthouse( vec3 p )
{

    vec3 q = lighthousePosition(p);

    // Foundation
    vec3 lighthouse = sdLighthouseBase(q);

    // Tower
    vec3 tower = sdLighthouseTower(q);
    lighthouse = pickClosest(lighthouse, tower);

    // Protection wall
    vec3 qw = q;
    qw.xz = -q.zx;
    vec3 wall = vec3(sdBox(qw - vec3(-3.4, 7.6 + 1.5 - 15.0, -5.0), vec3(0.35, 1.2, 6.0)), MATERIAL_LIGHTHOUSE_CONCRETE, 0.0);
    lighthouse = pickClosest(lighthouse, wall);

    // Silos
    vec3 qs = q - vec3(2.5, -3.9, 0.0);
    vec3 h = vec3(2.0, 0.0, 0.0);
    float silof = sdBox(qs - vec3(2.0, 0.0, 0.5), vec3(1.0, 4.0, 1.5));
    qs.z += 1.0;
    float silo1 = sdCylinder(qs - clamp(qs, -h, h), vec2(1.2, 4.0));
    qs.z -= 3.0;
    float silo2 = sdCylinder(qs - clamp(qs, -h, h), vec2(1.2, 4.0));

    silof = smin(silof, min(silo1, silo2), 0.1);
    silof = max(silof, -sdBox(qs - vec3(2.0, -1.1, -1.5), vec3(1.5, 2.0, 0.55)));

    vec3 silo = vec3(silof - 0.05, MATERIAL_LIGHTHOUSE_CONCRETE, 0.0);
    silo.x = min(silo.x, sdBox(qs + vec3(-0.8, 1.0, 1.5), vec3(1.0, 2.2, 1.0)));

    lighthouse = pickClosest(lighthouse, silo);

    // Door
    vec3 door = vec3(sdBox(qs + vec3(-1.8, 1.4, 1.5), vec3(0.05, 1.6, 0.4)), MATERIAL_LIGHTHOUSE_DOOR, 0.0);
    lighthouse = pickClosest(lighthouse, door);

    // Platform railings
    float guardrails = sdGuardRails(q - vec3(0.0, 20.2, 0.0), 3.8);
    lighthouse = pickClosest(lighthouse, vec3(guardrails, MATERIAL_LIGHTHOUSE_METAL, 0.0));

    // Lantern

    vec3 lantern = sdLantern(q);

    lighthouse = pickClosest(lighthouse, lantern);

    return lighthouse;

}


float getLighthouseBumpMap( vec3 p, vec3 ob )
{

   vec3 q = p - lighthouseOrigin;
   q.xz = mat2(0.7071, -0.7071, 0.7071, 0.7071) * q.xz;
   float an = atan(q.x, q.z);

   vec2 qa = vec2(cos(an), sin(an)) * p.y * 32.0 + p.y;
   float n = FBM( qa, 3, 0.75 );

   float b = 0.0
           + n * 0.02 * float(ob.y == MATERIAL_LIGHTHOUSE_WINDOW)
           + n * 0.1  * float(ob.y == MATERIAL_LIGHTHOUSE_CONCRETE);

   if (ob.y == MATERIAL_LIGHTHOUSE_MASONRY) 
   {
       vec2 brick = brickPattern(q, ob);

       float depth = 0.4 - step(3.0, ob.z) * 0.3 - step(4.0, ob.z) * 0.098;

       b += (min(brick.x, brick.y) + brick.x * 0.25) * depth;

    }

    return b;
}





// OCEAN SDF


float sineSquaredPos(float v)
{

    return pow(abs(0.5 + v * 0.5), 2.0);

}


float sineSquared(float v)
{

    return -1.0 + sineSquaredPos(v) * 2.0;

}


float sineB(vec2 rp, float time) {

  float a = sin((rp.x * 0.117351 + rp.y * 0.917892) + time);
  float b = sin((rp.x * -0.991752 + rp.y * 0.123639) + time);

  return mix(a, b, 0.6667);

}


float sineA(vec2 q, float time) {

  float o = sin(q.y + time);

  float s = sin(q.x + time);

  s += sineSquared(sin(2.0 * q.x + 0.4971 + o)) * 0.5;
  s += sineSquared(sin(4.0 * q.x + 1.1238 + o)) * 0.25;
  s += sin(8.0 * q.x + 2.5781 + o) * 0.125;
  //s += sin(16.0 * q.x + 4.6823 + o) * 0.0625;

  return s;

}


float[9] getOceanTimes(float offset)
{
    float t0 = (iTime + offset) * 0.6;
    float t1 = t0 * 0.5;
    float t2 =  1.0 + -t0 * 0.25;
    float times[9] = float[9](
        t0,
        t1,
        -t0,
        -t1,
        t2,
        t2 * 5.0,
        t1 * 0.1,
        t1 * 0.5,
        t2 * 0.25
      );
    return times;
}


float wave(float x, float t)
{
    float p = x + t;
    float gtx = cos(p);
    float gty = sin(p);

    return sin(p - gtx) + gty;
}

float lighthouseSwell(vec3 rp, float t1, float t2, float swell, float waves)
{
  vec3 lq = lighthousePosition(rp);
  float ld = clamp(sdLighthouseFoundation2D(lq.xz), 0.0, 100.0);
  float d = 50.0 / (10.0 + ld);

  float h = 0.0;

  d *= (1.5 + (swell + waves) * 0.1);
  float x = ld * 0.2;

  h += d * 0.25 * (0.5 + waves * 0.5) * (0.5 + swell * 0.5);

  // radial wave pattern against the base of the lighthouse
  float a = wave(x,        t1);
  float b = wave(x * 4.0,  t1) * 0.1;

  h += a + b;

  // wave pattern at the circumference of the lighthouse
  vec2 rq = rp.xz - lighthouseOrigin.xz;
  float an = -atan(rq.y, rq.x);

  float a2 = wave(an,        t2);
  float b2 = wave(an * 4.0,  t2) * 0.1;
  float c2 = wave(an * 16.0, t2) * 0.04;

  h += a2 + b2 + c2;

  return h * 0.1 * d + exp(-pow(ld, 2.0)) * (ld + 1.0) * 0.25;

}



float oceanSurface(vec3 rp, float[9] times) {

    //return 0.0;

    float  t0 = times[0];
    float  t1 = times[1];
    float _t0 = times[2];
    float _t1 = times[3];

    float t2 = times[4];
    float t3 = times[5];

    float cost = cos(t0);
    float sint = sin(t0);

    float h = 1.0;
    rp.x += cost * 0.5;

    float swell = wave((rp.x + rp.z * 0.5) * 0.025,  t1);
    float waves = wave((rp.x + rp.z * 0.8) * 0.050,  t0);

    h += swell * 0.5;
    h += waves * 1.5;

    float windnoise = 0.0;

    float wt1 = _t0;
    vec2 wq1 = vec2(rp.x, rp.z * 1.2);
    float x1 = (wq1.x + wq1.y) * 0.2;
    float a1 = wave(x1 * 1.1,   wt1);
    float b1 = wave(x1 * 3.9,   wt1) * 0.1;

    windnoise += (a1 + b1) * 0.15;

    float wt2 = _t1;
    vec2 wq2 = vec2(rp.x, rp.z * 0.3 + cost);
    float x2 = (wq2.x + wq2.y) * 0.2 - waves * 0.25;
    float a2 = wave(x2 * 1.0,  wt2);
    float b2 = wave(x2 * 4.0,  wt2) * 0.1;

    windnoise += (a2 + b2) * 0.25;

    h += windnoise * (1.0 + (swell + waves) * 0.1);

    h += lighthouseSwell(rp, t2 * 4.0, t3, swell, waves) * 0.8;

    return h;
}


vec3 sdOcean( vec3 p, float[9] times )
{
    return vec3(p.y - oceanSurface(p, times), MATERIAL_SEA, 0.0);
}


float getOceanBumpMap( vec3 rp, float[9] times )
{
  //return 0.0;

  vec3 lq = lighthousePosition(rp);
  float sd = sdLighthouseFoundation2D(lq.xz);

  float d = 50.0 / (10.0 + sd);
  vec2 p0 = vec2(rp.z, rp.x * 1.2) * 0.4;

  // general noise
  //  * -sin(sd * 0.001 + times[3] * 0.02)
  float t = times[3];

  float gn0 = FBM(p0 + t * 1.5, 4, 0.55 );
  float gn1 = FBM(p0 + t * sin(sd * 0.001 * d - t * 0.002) + gn0 * 4.0, 4, 0.5 );

  return gn0 * (0.5 + 0.5 * gn1) * d * 0.5;
}


vec3 oceanShading(vec3 ro, vec3 rd, vec3 rp, vec3 n, float t, float[9] times)
{
    //return vec3(0.18);
    vec3 q = rp - lighthouseOrigin;
    float sd = sdLighthouseFoundation2D(q.xz) + 10.0;

    // lighthouse wash

    float d = 50.0 / (10.0 + sd);
    float r = sd + sin(iTime);
    float an = atan(q.x, q.z) - iTime * 0.02;
    float ns = FBM( vec2(cos(an) * r, sin(an) * r) * 0.1, 5, 0.9 );

    sd = sd * ns;

    // Initial color
    float ht = 1.0 - exp(-rp.z * 0.01);
    vec3 col = mix(vec3(0.16, 0.18, 0.19), vec3(0.14, 0.16, 0.17), ht);

    // general noise
    vec2 p0 = vec2(rp.z, rp.x * 1.2) * 0.3;
    //float gn = FBM(p0+ times[3] * exp(-sd * 0.001), 4, 0.5 );

    float tg = times[3];
    float gn0 = FBM(p0 + tg, 4, 0.5 );
    float gn1 = FBM(p0 + tg * sin(sd * 0.001 * d - tg * 0.002) + gn0 * 2.0, 4, 0.5 );

    float gn = gn0 * gn1;

    // turbulent water -- deeper water        
    float d0 = smoothstep(30.0, 90.0 + (10.0 * sin(iTime * 0.4896)), sd);
    col = mix(vec3(0.26, 0.3, 0.33) * (0.25 + (1.0 - d0) * 1.1 * (0.5 + 0.5 * gn)), col, pow(d0, 2.0));

    // turbulent water -- foamy water
    float ns0 = FBM( vec2(rp.x + iTime * 1.4783, rp.z + iTime * 0.674) * 0.05, 4, 0.65 );
    ns0 = pow(ns0, 2.0);

    float ns1 = FBM( vec2(rp.x, rp.z - iTime * 1.2233) * 0.1, 5, 0.75 );
    ns1 = smoothstep(0.25 - sin(iTime * 0.9235) * 0.05, 1.0, ns1);

    float d1 = 1.0 - smoothstep(25.0, 30.0 + ns * 20.0, sd);
    col = mix(col, vec3(0.58, 0.6, 0.62), pow(d1, 2.0) * ns1 * ns0 * (0.5 + 0.5 * gn));

    // Wave highlights
    col *= (1.0 + smoothstep(0.2, 0.9, ns1) * smoothstep(-5.0, 15.0, rp.y - 2.0) * 0.2 * step(0.0, rp.y));

    // wave sub-surface
    col = mix(col, vec3(0.15, 0.4, 0.5) * 0.5, d0 * max(0.0, dot(reflect(rd, n), n)));

    col *= (0.8 + 0.2 * ns * ns0);

    return col;
}


// RENDERING FUNCTIONS


vec3 applyFog(vec3 rd, inout vec3 col, vec3 skyColor, float t)
{
    float ns = FBM( vec2(rd.x + iTime * 0.1, rd.y) * 0.5, 4, 0.65 );

    float fog = pow(smoothstep(200.0, 600.0, t), 0.5);

    vec3 fogColor = vec3(0.67, 0.68, 0.67) * skyColor + (0.35 * ns - 0.25);
    fogColor = fogColor * exp(-vec3(1.0, 1.0, 0.95) * t * 0.00002);

    fogColor = smoothstep(-0.1, 1.15, fogColor);

    return mix(col, fogColor, fog);
}


vec3 scenePoint( vec3 p, float[9] times )
{
    vec3 d = vec3(MAX_DIST, MATERIAL_SKY, 0.0);

    vec3 ocean = sdOcean(p, times);

    // First checking if we're within a reasonable distance
    // from the lighthouse before evaluate its SDF
    vec3 lq = p - lighthouseOrigin;
    if (dot(lq, lq) < 1225.0) d = pickClosest(d, sdLighthouse(p));

    d = pickClosest( ocean, d);

    return d;
}



