float softshadowOcean( in vec3 ro, in vec3 rd, float mint, float maxt, float w, float[9] times )
{
    float res = 2.0;
    float ph = 1e20;
    float t = mint;

    for( int i=0; i<64 && t<maxt; i++ )
    {
        float h = scenePoint(ro + rd*t, times).x;

        if( h<0.001 * t )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, d/(w*max(0.0,t-y)) );
        ph = h;
        t += h;
    }

    return res;
}


float softshadowLighthouse( in vec3 ro, in vec3 rd, float mint, float maxt, float w )
{
    float res = 2.0;
    float ph = 1e20;
    float t = mint;

    for( int i=0; i<128 && t<maxt; i++ )
    {
        float h = sdLighthouse(ro + rd*t).x;
        if( h<0.001 * t )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, d/(w*max(0.0,t-y)) );
        ph = h;
        t += h;
    }

    return res;
}


// https://blog.devgenius.io/some-interesting-raymarching-concepts-8e38aa67e1ef
float ao(vec3 ro, vec3 n, float k)
{
    float res = 0.0;
    float f = 0.25;

    float times[9] = getOceanTimes(0.0);

    for(int i=1; i<6; i++)
    {
        vec3 rp = ro + f * float(i) * n;
        //res+=(1.0 / pow(2.0, float(i))) * (f * float(i) - min(sdLighthouse(rp).x, rp.y)); 
        res+=(1.0 / pow(2.0, float(i))) * (f * float(i) - scenePoint(rp, times).x);    
    }
    return k*res;
}


float getLanternLighting(vec3 ro, vec3 rd, vec3 rp, vec3 n, vec3 ob)
{

    vec3 toLantern = rp - lighthouseLantern;
    float toLanternDist = length(toLantern);
    toLantern = normalize(toLantern);

    vec3 beam = beamVector(0.0);
    float beamAlignment = 0.25 + abs(dot(toLantern, beam)) * 0.75;
    float normalAlignment = max(0.0, dot(-toLantern, n));

    normalAlignment = mix(normalAlignment, 0.8 + normalAlignment * 0.2, float(ob.y == MATERIAL_LIGHTHOUSE_LANTERN_GLASS));

    return beamAlignment * normalAlignment;

}


const vec3 keyLightColor = normalize(vec3(1.0, 0.8, 0.5));
const vec3 fillLightColor = normalize(vec3(0.8, 1.0, 1.2));
const vec3 ambtLightColor = normalize(vec3(0.92, 0.98, 1.1)) * 1.75;
const vec3 lntnLightColor = normalize(vec3(1.4, 1.2, 1.0));


vec3 getOceanLighting(vec3 ro, vec3 rd, vec3 rp, vec3 n, float[9] times)
{
    vec3 fillight = normalize( vec3(-keylight.x, 0.0, keylight.z * 0.5) );

    float amb = 0.5 + n.y * 0.5;

    float key = max(0.0, dot( -keylight, n ));
    float fil = max(0.0, dot( -fillight, n ));

    vec3 nro = rp + n * 0.01;
    float shd = softshadowOcean(nro, -keylight, 0.0, MAX_DIST, 0.1, times);

    float ksp = key * pow(max(0.0, dot(-keylight, reflect(rd, n))), 3.0);
    float kfrsnl = pow(max(0.0, dot(rd, -keylight)), 5.0);

    vec3 l = vec3(0.0); 
    l += key * 0.7 * shd * keyLightColor;
    l += fil * 0.3 * fillLightColor;
    l += ksp * kfrsnl * shd;
    l += amb * ambtLightColor;

    return l;
}



float getWindowIllumination(vec3 rp, vec3 n, vec3 ob)
{

    float idw = ob.z;
    float ho = idw * 0.13;

    vec3 innerLightOffset = vec3(0.0, (idw) * 3.8 + 2.0, 0.5);
    innerLightOffset.xz = innerLightOffset.xz * mat2(0.7071, -0.7071, 0.7071, 0.7071);

    vec3 q = rp - lighthouseOrigin - innerLightOffset;

    vec3 light = normalize(q);
    float windowExposure = max(0.0, dot(n, -light));

    // Window illumination applies to two windows
    float litWindows = (step(0.0, idw) - step(2.0, idw));

    return windowExposure * litWindows;
}


vec3 getLighthouseLighting(vec3 ro, vec3 rd, vec3 rp, vec3 n, vec3 ob)
{

    vec3 fillight = normalize( vec3(-keylight.x, keylight.y, keylight.z * 0.1) );

    float amb = 0.8 + max(0.0, n.y) * 0.2;

    float key = max(0.0, dot( -keylight, n ));

    float isWindow = float(ob.y == MATERIAL_LIGHTHOUSE_WINDOW || ob.y == MATERIAL_LIGHTHOUSE_WINDOW_CASEMENT);

    vec3 q = lighthousePosition(rp);
    float fillExposure = max(0.0, dot( -fillight, normalize(q))) * (1.0 - isWindow);
    float fil = max(0.0, dot( -fillight, n )) * fillExposure;

    vec3 nro = rp + n * 0.01;
    float shd = softshadowLighthouse(nro, -keylight, 0.0, MAX_DIST, 0.1);

    float occ = -clamp(ao(rp, n, 1.5), 0.0, 1.0);

    float ltn = getLanternLighting(ro, rd, rp, n, ob);
    ltn *= smoothstep(35.5, 38.0, rp.y);

    float wdw = getWindowIllumination(rp, n, ob) * isWindow;

    float isLanternGlass = float(ob.y == MATERIAL_LIGHTHOUSE_LANTERN_GLASS);

    amb *= 1.0 + 0.5 * isLanternGlass;
    shd = mix(shd, 1.0, isLanternGlass);

    vec3 l = vec3(0.0); 
    l += key * 0.7 * shd * keyLightColor;
    l += fil * 0.3 * fillLightColor;
    l += amb * ambtLightColor * 0.8;
    l += occ * 0.5;
    l += ltn * 3.0 * lntnLightColor;
    l += wdw * 16.0;

    return l;

}



float getBrickWetness( vec3 rp )
{

    float wy0 = oceanSurface(rp, getOceanTimes( 0.0));
    float wy1 = oceanSurface(rp, getOceanTimes(-1.0));
    float wy2 = oceanSurface(rp, getOceanTimes(-2.0));
    float wy3 = oceanSurface(rp, getOceanTimes(-4.0));

    float d0 = 1.0 - smoothstep(0.0, 0.5, rp.y - wy0);
    float d1 = 1.0 - smoothstep(0.0, 1.0, wy1 - rp.y);
    float d2 = 1.0 - smoothstep(0.0, 2.0, wy2 - rp.y);
    float d3 = 1.0 - smoothstep(0.0, 4.0, wy3 - rp.y);

    return 1.0 - (d0 + d1 + d2 + d3) * 0.25;

}


vec3 lighthouseShading(vec3 col, vec3 ro, vec3 rd, vec3 rp, vec3 n, float t, vec3 ob)
{

    float l = 0.5;
    float detail = exp(-t * 0.005);

    vec3 q = rp - lighthouseOrigin;
    q.xz = mat2(0.7071, -0.7071, 0.7071, 0.7071) * q.xz;

    float r = length(q);
    float an = atan(q.z, q.x);
    vec2 qa = vec2( cos(an), sin(an) ) * (q.y + 25.0 + r * 10.0);

    float n0 = FBM(qa * 0.05, 5, 0.8);

    vec2 qa1 = qa * vec2(n0 * 0.3, n0 * 0.5);
    float n1 = FBM(qa1 * 0.02, 6, 0.7);

    vec2 qa2 = qa * vec2(n0, n1 * 20.0);
    float n2 = FBM(qa2 * 8.0, 2, 0.3);

    float n11 = smoothstep(0.60, 1.0, n1);
    float n12 = smoothstep(0.50, 1.0, n1);
    float n13 = smoothstep(0.55, 1.0, n1);

    float nbr = max(min(n0, 1.0 - n2 * 0.75), 1.0 - n11);
    float nbg = max(min(n0, 1.0 - n2), 1.0 - n12);
    float nbb = max(min(n0, 1.0 - n2 * 0.5), 1.0 - n13);

    vec3 nb = vec3(nbr, nbg, nbb) * (0.5 + 0.5 * n2);

    vec3 brickColor = vec3(0.31, 0.32, 0.3)
        * (0.75 + 0.5 * (1.0 - nb))
        * (0.6  + 1.0 *        n2)
        * (0.9  + 0.1 *        vec3(n12, n13, n11))
        ;

    vec3 paintColor = vec3(0.6, 0.6, 0.62) * (1.1  - 0.2 * nb);

    if (ob.y == MATERIAL_LIGHTHOUSE_METAL)
    {
        col = vec3(0.06, 0.08, 0.1)
            * (0.5 + 2.0 * n13 * nb * n2)
            + n2 * 0.2;
    }
    else if (ob.y == MATERIAL_LIGHTHOUSE_CONCRETE)
    {
        col = vec3(0.17, 0.2, 0.18) 
            * (0.75 + 0.5 * n1)
            * (0.8  + 0.4 * nb)
            + n1 * 0.1;
        col *= (0.75 + nb.bgr * (1.0 - nb));
    }
    else if (ob.y == MATERIAL_LIGHTHOUSE_DOOR)
    {
        col = vec3(0.0, 0.2, 0.1) * n2;
    }
    else if (ob.y == MATERIAL_LIGHTHOUSE_MASONRY) 
    {

        if (ob.z == 1.0) 
        {
            // foundation - dark

            float wtn = getBrickWetness(rp);
            wtn = smoothstep(0.1, 0.8, wtn) * (1.0 - smoothstep(7.0, 12.0, rp.y));

            // Base color
            vec2 brick = brickPattern(q, ob);

            float brick1 = 1.0 - step(0.6, brick.x);
            float brick2 = step(0.5, brick.x) - step(0.8, brick.x);
            float brick3 = step(0.8, brick.x);

            col = brickColor;
            col = mix(col, brickColor * vec3(1.16, 1.08, 0.99),  brick1); 
            col = mix(col, brickColor * vec3(1.08, 1.05, 1.03),  brick2);
            col = mix(col, brickColor * vec3(0.95, 0.85, 0.75),  brick3);

            col = mix(col, col * (0.5 + nb * 0.5), 1.0 - brick.x);
            col = col * (1.0 - 0.75 * pow(brick.x, 2.0));

            // Water streaking
            //float downstream = sin(rp.y * (1.1 + rd.y) + n0) * 0.2 + sin(rp.y * (4.0 - rd.y) + n1) * 0.1 + sin(rp.y * 24.0) * 0.05;

            //float anw = atan(q.z, q.x);
            //vec2 qw = vec2(cos(anw), sin(anw));
            //float nsw0 = FBM( qw * 30.0 + rp.y * 1.2 + iTime, 3, 0.8 );
            //float nsw1 = FBM( qw * 15.0 + rp.y * 0.3 + iTime * 0.5 + nsw0 * 4.0, 3, 0.4 );

            float nsw0 = FBM( vec2(atan(q.z, q.x + n1) * 5.0 + n1 * 5.0, rp.y * 1.1 + n2 * 0.5 + iTime + n0), 3, 0.8 );
            float nsw1 = FBM( vec2(atan(q.z + n1, q.x) * 30.0 + nsw0 * 1.0, rp.y * 0.1 + iTime * 0.5 - nsw0 * 5.0), 3, 0.4 );
            nsw1 = smoothstep(0.3, 0.4, nsw1) * (0.9 + 0.1 * brick.y);
            nsw0 *= nsw1;

            col = col * smoothstep(-4.0, 8.0 + n1 * 2.0, rp.y);
            col *= (n0 * 0.5) + smoothstep(0.3, 0.7, brick.x) * n1 * 1.0;

            float streaks = smoothstep(0.8, 0.9, n1) * smoothstep(0.75, 0.85, n1);
            vec3 rust = vec3(0.11 + n2 * 0.1, 0.1 + n0 * 0.1, 0.08 - n2 * 0.04) * nb.rgg;
            col = mix(col, rust, streaks * 0.7) * (0.5 + nb.ggg * 0.75);

            col += brick.y * (0.35 + n13 * 0.25) * (1.0 - n11) * nb.rgb * 0.65;

            // water leakage
            col = col - wtn * 0.25; // wet stone darkening
            col = mix(col, vec3(0.33, 0.37, 0.375) * nsw1, smoothstep(0.0, 0.6, wtn) * wtn * nsw0); // water streaks
        }
        else if (ob.z == 3.0)
        {
            // tower - dark section

            vec2 brick = brickPattern(q, ob);

            float brick1 = 1.0 - step(0.6, brick.x);
            float brick2 = step(0.5, brick.x) - step(0.8, brick.x);
            float brick3 = step(0.8, brick.x);

            col = brickColor;
            col = mix(col, brickColor * vec3(1.16, 1.08, 0.99),  brick1); 
            col = mix(col, brickColor * vec3(1.08, 1.05,  1.03),  brick2);
            col = mix(col, brickColor * vec3(0.95, 0.85, 0.75),  brick3);

            col = mix(col, col * (0.5 + nb * 0.5), 1.0 - brick.x);
            col = col * (1.0 - 0.75 * pow(brick.x, 2.0));

            float streaks = smoothstep(0.8, 0.9, n1) * smoothstep(0.75, 0.85, n1);
            vec3 rust = vec3(0.11 + n2 * 0.1, 0.1 + n0 * 0.1, 0.08 - n2 * 0.04) * nb.rgg;
            col = mix(col, rust, streaks * 0.6) * (0.75 + nb.ggg * 0.75);
            col *= 0.1 + smoothstep(9.0, 18.0 + n2 * 0.5, rp.y + 2.0) * 0.75; // water darkening

            col += brick.y * n2 * (1.2 - n13) * (1.0 - nb.brg * 0.75);

        }
        else if (ob.z == 4.0)
        {
            // tower - white section

            col = paintColor;

            vec2 brick = brickPattern(q, ob);

            col = col * (0.95 + brick.x * 0.05);

            vec3 fade = smoothstep(0.6, 1.0, n11) * nb.rbg * (0.5 + n2 * 0.5);
            col = mix(col, brickColor * vec3(0.25, 0.45, 0.5), clamp(fade, 0.0, 1.0) * 0.5);
            col = mix(col, paintColor, brick.y);
            col *= (0.9 + 0.2 * nb);

            // stripes
            float roughEdge = n2 * 0.02;
            float stripe = min(
                abs(rp.y - 24.65) - 0.14 - roughEdge, 
                abs(rp.y - 27.65) - 0.14 - roughEdge
                );

            // letters

            vec2 lpos = vec2( atan(q.z/q.x) * 1.9 + 0.52, (rp.y - 23.95) / 2.2 );
            float blackPaint = min(stripe, paintedLetters(lpos));

            col = col * (1.0 - (0.9 - n1 * n2 * 0.25) * step(0.0, -blackPaint));

        }
        else if (ob.z == 2.0)
        {
            // tower - lanter base

            vec2 brick = brickPattern(q, ob);

            float brick1 = 1.0 - step(0.6, brick.x);
            float brick2 = step(0.5, brick.x) - step(0.8, brick.x);
            float brick3 = step(0.8, brick.x);

            col = brickColor;
            col = mix(col, brickColor * vec3(1.16, 1.08, 0.99),  brick1); 
            col = mix(col, brickColor * vec3(1.08, 1.05,  1.03),  brick2);
            col = mix(col, brickColor * vec3(0.95, 0.85, 0.75),  brick3);

            col = mix(col, col * (0.5 + nb * 0.5), 1.0 - brick.x);
            col = col * (1.0 - 0.75 * pow(brick.x, 2.0));
            col += brick.y * n2 * nb;

        }
        else if (ob.z == 5.0)
        {
            // Painted black
            col = vec3(0.1, 0.1, 0.15) * (0.5 + 0.5 * n2) + (1.0 - nb) * 0.2;
        }
        else
        {
            col = brickColor * (0.75 + 0.25 * n12) - (1.0 - nb) * 0.25;
        }

    }
    else if (ob.y == MATERIAL_LIGHTHOUSE_WINDOW_CASEMENT)
    {
        col = vec3(0.7, 0.7, 0.75);
    }
    else if (ob.y == MATERIAL_LIGHTHOUSE_WINDOW) 
    {

        float n01 = smoothstep(0.6, 0.7, n0);
        float n11 = smoothstep(0.4, 0.7, n1);

        vec3 qw = q;
        //float step = 3.8;
        qw.y = qw.y - 2.2;

        float idw = ob.z;
        float ho = idw * 0.13;

        // Window illumination, applies to two windows
        float litWindows = (step(0.0, idw) - step(2.0, idw));

        vec3 paintedCol = paintColor;
        vec3 fade = smoothstep(0.7, 1.0, n11) * nb.rbg * (0.8 + n2 * 0.2);
        paintedCol = mix(paintedCol, paintedCol * vec3(0.75, 0.9, 1.0) * 2.0, clamp(fade, 0.0, 1.0) * 0.1);

        vec3 unpaintedCol = brickColor * (0.5 + 0.5 * nb) * (0.75 + 0.25 * n12);
        unpaintedCol *= 0.25 + smoothstep(12.0, 15.0 + n01 * 0.5, rp.y) * 0.75; // water darkening

        float streaks = smoothstep(0.8, 0.9, n1) * smoothstep(0.75, 0.85, n1);
        unpaintedCol = mix(unpaintedCol, vec3(0.11, 0.1, 0.08), streaks * 0.4) * (0.75 + nb.ggg * 0.75);

        vec3 windowCol = mix(
            unpaintedCol,  // unpainted
            paintedCol,    // painted
            step(0.1, idw)
            );

        vec3 unlitPane = getSkyColor(reflect(rd, n));
        vec3 windowPaneCol = mix(
            unlitPane,              // unlit
            vec3(1.35, 1.125, 0.9), // lit
            litWindows
        );

        float paneBoundary = step(3.38, length(qw.xz) + ho);

        col = mix(windowPaneCol, windowCol, paneBoundary);       

    }

    return col;
}



vec4 raymarchLighthouse( vec3 ro, vec3 rd, vec3 col )
{

    float toLighthouse = length(lighthouseOrigin - ro);

    vec3 rp = ro + rd * toLighthouse;
    vec3 d = sdLighthouse(rp);

    if (d.x > 10.0 - rd.y * 10.0) 
    { 
        return vec4(col, MAX_DIST); 
    }

    float t = toLighthouse - abs(d.x) - 15.0;


    for ( int i=0; i<150; i++)
    {

        rp = ro + rd * t;

        d = sdLighthouse(rp);

        if (abs(d.x) < 0.0004) break;

        if (t > toLighthouse + 30.0)
        {
            t = MAX_DIST;
            break;
        }

        t = max(1.0, t) + d.x;

    }

    if (t < MAX_DIST)
    {
        vec3 n = calcLighthouseNormal(rp, t, d);
        vec3 l = getLighthouseLighting(ro, rd, rp, n, d);

        vec3 mt = lighthouseShading(col.rgb, ro, rd, rp, n, t, d);

        col = mt * l;
    }

    return vec4(col, t);
}


vec3 renderLanternGlass( vec3 ro, vec3 rd, vec3 col, inout float lt )
{

    float toLantern = length(lighthouseLantern - ro);
    vec3 rp = ro + rd * toLantern;

    float tmax = toLantern * 1.5;

    vec3 d = sdLanternGlass(rp);

    if (d.x > 2.0) return col; 

    float t = toLantern - abs(d.x) - 2.0;

    int   ps = 0;    // pass throughs
    float ft = 1e20; // first hit
    float dt = 0.0;  // distance travelled through glass
    float ior = 1.0/1.5;
    float oir = 1.5;
    vec3 n = vec3(0.0);

    bool enteredGlass = false;

    for ( int i=0; i<1000; i++)
    {
        rp = ro + rd * t;

        d = sdLanternGlass(rp);

        if (d.x <= 0.01) 
        {    
            ft = min(t + d.x, ft);

            dt += 0.001 + step(41.5, rp.y) * 0.004;
            d.x = 0.001;

            enteredGlass = true;
        }
        else if (d.x > 0.01 && enteredGlass == true)
        {
            ps += 1;
            enteredGlass = false;
        }


        if (ps > 2 || (ps > 0 && lt < ft) || t > tmax) break;

        t = max(1.0, t) + d.x;
    }

    if (ps > 0 && ft < lt)
    {
      vec3 gp = ro + rd * ft;

      // transparent color
      rd = normalize(rp - ro);
      vec3 absorb = exp(-dt * 10.0 * vec3(0.15, 0.16, 0.45));
      float cloudiness = 1.0 - exp(-dt * 0.3);
      vec3 opaqueColor = vec3(1.0 - cloudiness, (1.0 - cloudiness) * 0.75, (1.0 - cloudiness) * 0.5);
      col = mix(col * absorb, opaqueColor, cloudiness);

      vec3 gob = vec3(0.0, MATERIAL_LIGHTHOUSE_LANTERN_GLASS, 0.0);
      vec3 n = calcLanternGlassNormal(gp);
      vec3 gl = getLighthouseLighting(ro, rd, gp, n, gob);

      col *= gl;

      // sky reflection
      vec3 rrd = reflect(rd, n);
      float fresnel = pow(max(0.0, dot(rd, rrd)), 5.0);

      col = mix(col, getSkyColor(rrd), fresnel);

    }

    return col;
}


vec4 raymarchOcean( vec3 ro, vec3 rd, float times[9] )
{
    vec3 rp = ro + rd * MAX_DIST;

    vec3 d = sdOcean(rp, times);

    if (d.x > 0.0) return vec4(0.0, MATERIAL_SKY, 0.0, MAX_DIST);

    float t = (ro.y - 8.0) / rd.y;

    for ( int i=0; i<150; i++)
    {

        rp = ro + rd * t;
        d = sdOcean(rp, times);

        t = max(1.0, t) + d.x;

        if (abs(d.x) < 0.001 * t) break;

        if (t > MAX_DIST) break;

    }

    return vec4(d, t);
}


vec4 renderOcean( vec3 ro, vec3 rd, vec4 col )
{
    float times[9] = getOceanTimes(0.0);

    vec4 R = raymarchOcean(ro, rd, times);

    if (R.w < col.a)
    {
        vec3 rp = ro + rd * R.w;
        vec3 n = calcOceanNormal(rp, times);
        vec3 l = getOceanLighting(ro, rd, rp, n, times);

        vec3 mt = oceanShading(ro, rd, rp, n, R.w, times);

        col.rgb = mt * l;
        col.a = R.w;

        // Reflections
        vec3 rro = rp + n * 0.1;
        vec3 rrd = reflect(rd, n);
        vec4 rR = raymarchOcean(rro, rrd, times);

        vec3 rcol = getSkyColor(rrd);

        if (rR.w < MAX_DIST)
        {
            vec3 rrp = rro + rrd * rR.w;
            vec3 rn = calcOceanNormal(rrp, times);
            vec3 rl = getOceanLighting(rro, rrd, rrp, rn, times);

            vec3 rmt = oceanShading(rro, rrd, rrp, rn, rR.w, times);

            rcol = rmt * rl;

        }

        float radius = smoothstep(25.0, 50.0, length(rp.xz - lighthouseOrigin.xz));
        float reflects = 0.25 * radius * (1.0 - smoothstep(150.0, 300.0, R.w));
        float frsnl = pow(max(0.0, dot(rrd, rd)), 5.0);

        col.rgb = mix(col.rgb, rcol, reflects * frsnl); 

    }

    return col;
}


vec3 renderLightBeam(vec3 ro, vec3 rd, vec3 col, vec3 skycolor, inout float rt)
{
    vec3 q = beamVector(0.0);
    float d = sdLighhouseBeam(ro);

    float or = dot(rd, q);
    float dt = 0.5 + abs(or) * 1.0;

    float t = min(d, rt);
    vec3 rp = ro + rd * t;

    float dist = 0.0;

    for (int i=0; i<200; i++) {

        d = sdLighhouseBeam(rp);

        vec2 q = rp.xz - lighthouseLantern.xz;
        d = d / dot(q, q); // light falloff

        dist += max(0.0, -d / t);
        t += max(dt, d);

        if (t >= rt) break;

        rp = ro + rd * t;

    }

    dist = 1000.0 * dist;

    col *= 3.0 - exp(-dist * vec3(1.0, 0.8, 0.5)) * 2.1;

    return col;
}


void setCamera(vec2 uv, inout vec3 ro, inout vec3 rd)
{

    float cameraHeight = 100.0; // 15.0
    float cameraPitch = 0.2; // 0.2
    float focalLength = 40.0;

    float h = -2.5; // -2
    float v = -13.5; // 13

    ro = vec3(0.0, cameraHeight, -focalLength);
    ro.xy += uv * 35.0;

    //ro.z = -4.0;
    rd = normalize( vec3(h, v, focalLength));

    //ro.y += cameraHeight;

}


vec3 render( vec2 uv )
{
    // Camera
    vec3 ro = vec3(0.0);
    vec3 rd = vec3(0.0);

    setCamera(uv, ro, rd);

    // Ray marching and rendering the scene
    vec3 skycolor = getSkyColor(rd);

    vec4 L = raymarchLighthouse(ro, rd, skycolor);
    vec4 O = renderOcean(ro, rd, L);

    vec3 col = O.rgb; // scene color
    float t = O.a;    // ray depth

    col = applyFog(rd, col, skycolor, t);

    col = renderLightBeam(ro, rd, col, skycolor, t);

    col = renderLanternGlass(ro, rd, col, t);

    return col;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragCoord = fragCoord * 1.2;

    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = ((2.0 * fragCoord) - iResolution.xy) / iResolution.y;

    # ifdef AA
    vec2 dx = vec2(dFdx(uv.x) * 0.25, 0.0);
    vec2 dy = vec2(0.0, dFdy(uv.y) * 0.25);

    vec3 col1 = render(uv + dx - dy);
    vec3 col2 = render(uv - dx - dy);
    vec3 col3 = render(uv + dx + dy);
    vec3 col4 = render(uv - dx + dy);

    vec3 col = (col1 + col2 + col3 + col4) * 0.25;

    # else
    vec3 col = render(uv);

    # endif

    // Desaturate & color correct

    float L = dot(col, vec3(0.299, 0.587, 0.114));

    //col /= L;

    float exposure = log2(L / 0.5);
    exposure += 0.2;

    L = L * pow(2.0, exposure);

    col = mix(col, vec3(L), 0.35);
    col = vec3(
        pow(col.r, 0.82),
        pow(col.g, 0.85),
        pow(col.b, 0.85)
        );
    col = smoothstep(0.0, 0.8, col);

    // Lens falloff
    col *= 1.0 - 0.02 * dot(uv, uv);

    // Grain
    col *= 0.95 + staticNoise(uv) * 0.1;

    // Opening title
    if (SHOWTITLE) {

        if (paintedLetters(uv * vec2(1.4, 1.2) - vec2(-0.08, -0.75)) < 0.0)
        {
            col = mix(vec3(1.0), col, smoothstep(7.0, 7.5, iTime));   
        }

        if (iTime > 7.5)
        {
            SHOWTITLE = false;
        }

    }

    // Output to screen
    fragColor = vec4(col.rgb, 1.0);
}
