void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = texture(iChannel0, fragCoord / iResolution.xy).rgb;

    // apply exposure (how long the shutter is open)
    // color *= c_exposure;

    // convert unbounded HDR color range to SDR color range
    color = ACESFilm(color);

    // convert from linear to sRGB for display
    color = LinearToSRGB(color);

    fragColor = vec4(color, 1.0f);
}

const float c_pi = 3.14159265359f;
const float c_twopi = 2.0f * c_pi;

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float length6( vec3 p ) {
	p = p*p*p; p = p*p;
	return pow( p.x + p.y + p.z, 1.0/6.0 );
}

vec3 LessThan(vec3 f, float value) {
    return vec3(
        (f.x < value) ? 1.0f : 0.0f,
        (f.y < value) ? 1.0f : 0.0f,
        (f.z < value) ? 1.0f : 0.0f);
}

vec3 LinearToSRGB(vec3 rgb) {
    rgb = clamp(rgb, 0.0f, 1.0f);
    return mix(
        pow(rgb, vec3(1.0f / 2.4f)) * 1.055f - 0.055f,
        rgb * 12.92f,
        LessThan(rgb, 0.0031308f)
    );
}

vec3 SRGBToLinear(vec3 rgb) {
    rgb = clamp(rgb, 0.0f, 1.0f);
    return mix(
        pow(((rgb + 0.055f) / 1.055f), vec3(2.4f)),
        rgb / 12.92f,
        LessThan(rgb, 0.04045f)
	);
}

vec3 ACESFilm(vec3 x) {
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x + b)) / (x*(c*x + d) + e), 0.0f, 1.0f);
}

uint wang_hash(inout uint seed) {
    seed = uint(seed ^ uint(61)) ^ uint(seed >> uint(16));
    seed *= uint(9);
    seed = seed ^ (seed >> 4);
    seed *= uint(0x27d4eb2d);
    seed = seed ^ (seed >> 15);
    return seed;
}

float randomFloat01(inout uint state) {
    return float(wang_hash(state)) / 4294967296.0;
}

vec3 randomUnitVector(inout uint state) {
    float z = randomFloat01(state) * 2.0f - 1.0f;
    float a = randomFloat01(state) * c_twopi;
    float r = sqrt(1.0f - z * z);
    float x = r * cos(a);
    float y = r * sin(a);
    return vec3(x, y, z);
}

float rand(vec4 seed) {
    float dotProduct = dot(seed, vec4(12.9898, 78.233, 45.164, 94.673));
    return fract(sin(dotProduct) * 43758.5453123);
}

float sampleCosTheta(float g, float xi1) {
    if (g == 0.0) {
        return 1.0 - 2.0 * xi1;
    } else {
        float term = (1.0 - g * g) / (1.0 - g + 2.0 * g * xi1);
        return (1.0 / (2.0 * g)) * (1.0 + g * g - term * term);
    }
}

float samplePhi(float xi2) {
    return c_twopi * xi2;
}

vec3 computeNewDirection(float cosTheta, float phi) {
    float sinTheta = sqrt(1.0 - cosTheta * cosTheta);
    return vec3(
        sinTheta * cos(phi),
        sinTheta * sin(phi),
        cosTheta
    );
}

vec3 alignToDirection(vec3 newDir, vec3 omegaPrime) {
    vec3 zAxis = vec3(0.0, 0.0, 1.0);
    vec3 v = cross(zAxis, omegaPrime);
    float s = length(v);
    float c = dot(zAxis, omegaPrime);
    mat3 rotation;

    if (s < 0.00001) { // ê±°ì˜ 0ì— ê°€ê¹Œìš¸ ë•ŒëŠ” íšŒì „ ì—†ì´ ë°˜í™˜
        return newDir;
    }

    float invsSq = 1.0 / (s * s);
    mat3 vSkew = mat3(
        0.0, -v.z, v.y,
        v.z, 0.0, -v.x,
        -v.y, v.x, 0.0
    );

    // Rodrigues' rotation formula: R = I + vSkew + vSkew^2 * (1 - c) / s^2
    mat3 vSkewSq = vSkew * vSkew;
    rotation = mat3(1.0) + vSkew + vSkewSq * (1.0 - c) * invsSq;

    return rotation * newDir;
}

vec3 sampleHenyeyGreenstein(vec3 omegaPrime, float g, uint rngState) {
    float xi1 = randomFloat01(rngState);
    float xi2 = randomFloat01(rngState);
    float cosTheta = sampleCosTheta(g, xi1);
    float phi = samplePhi(xi2);
    vec3 newDir = computeNewDirection(cosTheta, phi);
    return alignToDirection(newDir, omegaPrime);
}

float calculateScatterProbability(float sigma_s, float delta_x) {
    return 1.0 - exp(-sigma_s * delta_x);
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);
	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

#define RAY_ITER 40
#define LIGHT_ITER 32
#define RAY_STEP_SIZE 0.03
#define WAVE_ATTEN vec3(0.3, 0.6, 0.9)
#define LIGHT_STEP 0.05

const float KEY_SPACE = 32.5/256.0;

void rotate(inout vec3 z, vec3 axis, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    mat3 rot = mat3(
        c + axis.x * axis.x * (1.0 - c),       axis.x * axis.y * (1.0 - c) - axis.z * s, axis.x * axis.z * (1.0 - c) + axis.y * s,
        axis.y * axis.x * (1.0 - c) + axis.z * s, c + axis.y * axis.y * (1.0 - c),       axis.y * axis.z * (1.0 - c) - axis.x * s,
        axis.z * axis.x * (1.0 - c) - axis.y * s, axis.z * axis.y * (1.0 - c) + axis.x * s, c + axis.z * axis.z * (1.0 - c)
    );
    z = rot * z;
}

float sphere(vec3 p) {
    p += vec3(-0.5, -1.1, 0.5);
    vec3 s = vec3(1.0, 0.3, 1.0);
    p = abs(p) - s;
    return max(p.x, max(p.y, p.z));
}

float box(vec3 p, vec3 s) {
    rotate(p, normalize(vec3(-1.0, 0.1, 0.1)), p.z * 3.0);
    rotate(p, normalize(vec3(0.0, 0.0, -1.3)), p.y * 1.5);
    p = abs(p) - s;
    return max(p.x, max(p.y, p.z));
}

float julia(vec3 p) {
    p *= 20.0;
    rotate(p, normalize(vec3(0.2, 0.5, 0.8)), 0.4);
    float len = length(p);
    float scale = 1.1;
    const int iterations = 5;
    float a = 30.0;
    float l = 0.0;
    vec2 rotationAnimAmp = vec2(0.05, 0.06);
    vec2 rotationPhase = vec2(
        cos((iMouse.x / iResolution.x - 0.5) * 3.14) * 0.01,
        0.15 + cos((iMouse.y / iResolution.y - 0.5) * 3.14) * 0.05
    );
    vec3 juliaOffset = vec3(-6.0, 0.1, -2.5);
    float angleX = 6.2 * iMouse.x / iResolution.x;
    float cosAngleX = cos(angleX);
    float sinAngleX = sin(angleX);
    float angleY = 6.1 * iMouse.y / iResolution.y;
    float cosAngleY = cos(angleY);
    float sinAngleY = sin(angleY);
    for (int i = 0; i < iterations; i++) {
        p = abs(p);
        p = p * scale + juliaOffset;
        {
            float y = p.y * cosAngleX - p.z * sinAngleX;
            float z = p.y * sinAngleX + p.z * cosAngleX;
            p.y = y;
            p.z = z;
        }
        pR(p.xz, rotationPhase.x * a * 2.1 + (iMouse.y / iResolution.y - 0.5) * 6.0);
        pR(p.yz, rotationPhase.y * 3.14 + sin(a * (iMouse.x / iResolution.x - 0.5) * 3.14 + len) * rotationAnimAmp.x * (iMouse.x / iResolution.x - 0.5) * 6.0);
        pR(p.zx, rotationPhase.y * 3.14 + sin(a * (iMouse.y / iResolution.y - 0.5) * 3.14) * rotationAnimAmp.x * (iMouse.y / iResolution.y - 0.5) * 6.0);
        l = length6(p);
    }
    return l * pow(scale, -float(iterations)) - 2.9;
}

float fractal(vec3 p) {
    float angle0 = 0.2;
    float cosAngle0 = cos(angle0);
    float sinAngle0 = sin(angle0);
    float angleX = 6.2 * iMouse.x / iResolution.x;
    float cosAngleX = cos(angleX);
    float sinAngleX = sin(angleX);
    float angleY = 6.1 * iMouse.y / iResolution.y;
    float cosAngleY = cos(angleY);
    float sinAngleY = sin(angleY);
    {
        float x = p.x * cosAngle0 - p.z * sinAngle0;
        float z = p.x * sinAngle0 + p.z * cosAngle0;
        p.x = x;
        p.z = z;
    }
    for (int i = 0; i < 6; i++) {
        {
            float y = p.y * cosAngleX - p.z * sinAngleX;
            float z = p.y * sinAngleX + p.z * cosAngleX;
            p.y = y;
            p.z = z;
        }
        {
            float x = p.x * cosAngleY - p.z * sinAngleY;
            float z = p.x * sinAngleY + p.z * cosAngleY;
            p.x = x;
            p.z = z;
        }
        float s1 = step(0.0, p.x + p.y);
        p.xy = mix(-p.yx, p.xy, s1);
        float s2 = step(0.0, p.y + p.z);
        p.yz = mix(-p.zy, p.yz, s2);
        float s3 = step(0.0, p.z + p.x);
        p.zx = mix(-p.xz, p.zx, s3);
        p -= 0.03;
    }
    return box(p, vec3(0.1, 0.6, 0.1));
}

float sdf(vec3 p) {
    return julia(p);
}

float sampleDensity(vec3 p) {
    float delta = 1.0;
    return max(-sdf(p), 0.0) * 20.0 + delta;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    uint rngState = uint(uint(fragCoord.x) * uint(1973) + uint(fragCoord.y) * uint(9277) + uint(iFrame) * uint(26699)) | uint(1);
    vec2 uv = fragCoord / iResolution.xy;
    uv = (uv - 0.5) * 2.0 * 2.0;
    uv.y *= iResolution.y / iResolution.x;
    vec3 col = vec3(0.0);
    vec3 camPos = vec3(0.0, 0.0, -2.0);
    vec3 rayPos = camPos;
    vec3 rayDir = normalize(vec3(uv, 0.0) - camPos);
    float time = iTime * 0.2;
    vec3 transmittance = vec3(1.0);
    rayPos += rayDir * (1.0 + randomFloat01(rngState));
    for (int i = 0; i < RAY_ITER; i++) {
        rayPos += rayDir * RAY_STEP_SIZE;
        float density = sampleDensity(rayPos);
        if (density == 0.0) {
            continue;
        }
        vec3 dTrans = exp(-density * RAY_STEP_SIZE * WAVE_ATTEN);
        vec3 lightDir = randomUnitVector(rngState);
        vec3 lightRayPos = rayPos;
        float shadowDensityAcc = 0.0;
        vec3 lightIntensity = vec3(0.0);
        for (int j = 0; j < LIGHT_ITER; j++) {
            if (sphere(lightRayPos) < 0.0) {
                lightIntensity = vec3(10.0);
                break;
            }
            float shadowDensity = sampleDensity(lightRayPos) * LIGHT_STEP;
            shadowDensityAcc += shadowDensity;
            lightRayPos += lightDir * LIGHT_STEP;
            float shadowScatterProbability = calculateScatterProbability(shadowDensity, LIGHT_STEP);
            if (shadowScatterProbability > randomFloat01(rngState)) {
                lightDir = sampleHenyeyGreenstein(lightDir, 0.6, rngState);
            }
        }

        vec3 externalLight = exp(-shadowDensityAcc * WAVE_ATTEN) * density * lightIntensity;
        externalLight = (externalLight - externalLight * dTrans) / density;
        col += transmittance * externalLight;
        transmittance *= dTrans;

        if (length(transmittance) < 0.1) {
            break;
        }

        float scatterProbability = calculateScatterProbability(density, RAY_STEP_SIZE);

        if (scatterProbability > randomFloat01(rngState)) {
            rayDir = sampleHenyeyGreenstein(rayDir, 0.6, rngState);
        }

    }

    bool spacePressed = (texture(iChannel2, vec2(KEY_SPACE, 0.25)).x > 0.1);

    vec4 lastFrameColor = texture(iChannel1, fragCoord / iResolution.xy);

    float blend = (lastFrameColor.a == 0.0f || spacePressed || iMouse.z > 0.0) 

        ? 1.0f : 1.0f / (1.0f + (1.0f / lastFrameColor.a));

    col = mix(lastFrameColor.rgb, col, blend);

    fragColor = vec4(col, blend);

}
