#define time (iTime*1000.)
#define resolution (iResolution.xy)

#define PI 3.1415
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	float offset = length(fragCoord/resolution-.5)*(1.5+sin(time/3000.));
	fragColor = (1.-offset)*vec4(.5*(1.+sin(time/500.)),.5*(1.+sin(time/500.+PI/3.)),.5*(1.+sin(time/500.+2.*PI/3.)),0); 
}
