#define pi 3.14159265


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y;

    float dist = length(uv);
    float angle = atan(uv.y, uv.x);
    float t = iTime;
    float s = mix(0.03, 0.15, abs(sin(t)));
    float n = 15.;
    float len = n * s;

    vec3 x;
    for (int i = 0; i < 3; i++)
    {
        float r = len / 2.0 / pi + float(i) * 0.02;

        uv = vec2(angle * len / 2.0 / pi + mix(-dist * 3., 0., tanh((dist * 3.0 + 1.) / 2.)), dist - r);
        uv.x = abs(mod(uv.x, s) - s / 2.0);

        x[i] = smoothstep(1., 0., pow(length(uv / 0.1), 0.8));
    }
	fragColor = vec4(x, 1.0);
}
