float circle(vec2 centre, float radius, vec2 fragCoord)
{
    vec2 delta = fragCoord - centre;
    float dist = length(delta);
    if ( dist < radius )
    {
        return 1.0 - (dist/radius);
    }
    else
    {
        return -1.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 centre = iResolution.xy * 0.5;
    float speed = 2.0;
    centre += centre * 0.8 * vec2(sin(iTime * 0.3 * speed) * cos(iTime * 0.7 * speed),
                                  sin(iTime * 1.3 * speed) * cos(iTime * 1.9 * speed));

    float radius = 15.0 + 10.0 * sin(iTime * 3.0) * cos(iTime * 7.0);

    float circ = circle(centre, radius, fragCoord);
    if ( circ >= 0.0 )
    {
        float scale = 0.1;

        float r = ((sin(iTime * 3.0 * scale) * cos(iTime * 7.0 * scale)) + 1.0) - 0.5;
        float g = ((sin(iTime * 13.0 * scale) * cos(iTime * 17.0 * scale)) + 1.0) - 0.5;
        float b = ((sin(iTime * 19.0 * scale) * cos(iTime * 3.0 * scale)) + 1.0) - 0.5;

        fragColor = vec4(((circ * 0.25) + 0.75)*vec3(r,g,b),1.0);
    }
    else
    {
        discard;
    }
}
