void mainImage( out vec4 c, vec2 g ) {	
    c *= 0.; //macOS fix;
    g = ceil(g / 8.) * 8. / iResolution.x - .4;

    float 
        k = iTime * 2.,
        f = g.x + sin(g.y * 4. + k + g.x) / 8.;

    c.rb += 1. - abs(f + f);

    if (f > .0 && f < .3)
    	c = fract(k - (f + k / 9. + sin(f + f + g.y)) * 2.) + vec4(.2, 0, .6, 0);    
    //c = (f > .0 && f < .3) ? vec4(.2, 0, .6, 0) + fract(k - (f + k / 9. + sin(f + f + g.y)) * 2.) : c;    
}

