void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y *= -1.;

    vec3 pixel = vec3(0.0);

    float height = texture(iChannel0,uv.xy).r;



    pixel = texture(iChannel1,uv.xy).rgb;

    //remove if for performance
   	float condition_if_1 = step(height, sin(iTime + 0.04));
	float condition_if_2 = step(height, sin(iTime));

    pixel = pixel * (1. - condition_if_1) + vec3(1.,1.,0.) * condition_if_1;
    pixel = pixel * (1. - condition_if_2);

    //if((height) < (sin(iTime + 0.04)))
    //{
        //pixel = vec3(1.,1.,0.);
    //}

    //if(height < sin(iTime))
    //{
        //pixel = vec3(0.,0.,0.);
    //}



	fragColor = vec4(pixel,1.0);
}
