vec3 polar_mix(vec3 a, vec3 b, float p) {
    return sqrt(p) * b + sqrt(1.0 - p) * a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 raw_uv = fragCoord.xy / iResolution.xy;
    vec2 uv = fract(vec2(2.0, 2.0) * raw_uv);
    vec3 bottom_left = vec3(1,0,0);
    vec3 bottom_right = vec3(0,0,1);
    vec3 top_left = vec3(0,1,0);
    vec3 top_right = vec3(0,0,0);
    vec3 color;
    if (raw_uv.x < 0.5) {
        vec3 bottom = mix(bottom_left, bottom_right, uv.x);
        vec3 top = mix(top_left, top_right, uv.x);
        color = mix(bottom, top, uv.y);
    }
    else {
        vec3 bottom = polar_mix(bottom_left, bottom_right, uv.x);
        vec3 top = polar_mix(top_left, top_right, uv.x);
        color = polar_mix(bottom, top, uv.y);
    }
    if (raw_uv.y < 0.5) {
        color = vec3(length(color));
    }
	fragColor = vec4(color,1.0);
}
