// #define hue(v)  ( .5 +      cos( 6.3*(v)  + vec4(0,23,21,0)  ) )
// #define hsv(c)  ( (c).z * mix( vec3(1), ( .6 + .6 * cos( 6.3*(c).x  + vec3(0,23,21)  ) ), (c).y ) )
// #define hsv(c)  ( (c).z * ( 1. + (c).y* ( .6 * cos( 6.3*(c).x  + vec3(0,23,21)  ) -.4 ) ) ) // golfed

void mainImage(out vec4 O, vec2 U) {
    O.xyz = iResolution;
    O = .6 + .6 * cos( 6.3 *  length(U+U-O.xz)/O.y  + vec4(0,23,21,0)  ); }

//  O = .5 + cos( 6.3 *  length(U+U-O.xz)/O.y  + vec4(0,23,21,0)  ) // full saturated hue
