// Plot color
float plot(vec2 uv, float pct) {
    float width = 0.01;
    return smoothstep(pct - width, pct, uv.y) - smoothstep(pct, pct + width, uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coord
	vec2 uv = fragCoord.xy / iResolution.xy;

    // dif. channels
    vec4 vid1 = texture(iChannel0, uv);
    vec4 vid2 = texture(iChannel1, uv);

    /******************************/
    // rays
    // Base color
    vec3 color =vec3(0.0);

    // percent from axis x position.
    vec3 pct = vec3(uv.x);

    // Plot transition lines
    color = mix(color, vec3(1.0, 1.0, 0.0), plot(uv, pct.r));

    vec4 chan = vec4(color, 1.0);
    /******************************/

    // porthole
    if (distance(fragCoord.xy, iMouse.xy) < 75.0)
    	fragColor = vid1;
    else
        fragColor = chan;

    // splice channels
    if (iFrame % 2 == 0)
        fragColor = mix(vid1, vid2, 0.75);




}

