void mainImage( out vec4 O,  vec2 U )
{
	O = texelFetch(iChannel0,ivec2(U),0); // r = rock, b = water
    O.r *= .5;
    O.g += .5*(O.r+O.b);
    if ( O.rgb == vec3(0) ) O++;
}

#define source a  // ab for pre-filled water layer, a for source
#define SIZE  10. // indeed, thinness ( 1/size ) compare to layer size
#define depth .2  // layer size

#define dens(p) 1.5*(4./3.1416)*p.x/L.x
#define rand2(p) fract(43758.5453123*sin((p)*mat2(127.1,311.7, 269.5,183.3)))
#define T(i,j) texelFetch(iChannel0, ivec2(u)+ivec2(i,j), 0 )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy;

    if ( texelFetch(iChannel0,ivec2(0),0).zw != R ) {     // must rebuilt setup
        if (u==vec2(.5)) O.zw = R;                        // to detect resol change
        vec2 U = u/R.y, L = R/R.y, p; 
        float N = SIZE * ceil(U.y*3.);                    // granularity in 3 regions
        U.y = fract(U.y*3.)/3. - .06;                     // current region
        U /= depth; L /= depth;                           // normalized thickness

        if ( 0. <= U.y && U.y <= 1. ) {                   // rock layer
            U *= N; L *= N;
            float v = 0.;
            for (int k = 0; k < 9; k++) {                 // seek in neighboors
                p = floor( mod( U+vec2(k%3-1,k/3-1), N*L) );
                for (float i = 0.; i<3.; i++)             // for proba > 1 ;-)
                    if ( rand2(p+.2*i+.1).x < dens(p)-i ) // if proba, hole
                        if ( length( U - p-rand2(p+.2*i) ) < .5) v = 1.; 
            }
            O.r = 1.-v; 
            O.a = 1.;                                     // diffusive region
        }             
        else if ( U.y> 1. && U.y < 1.1 ) O.source++;      // top water zone
        return;
    }

	O = texelFetch(iChannel0,ivec2(u),0);
    if ( u == vec2(.5) ) return;
    if ( u.y+.5 == R.y && u.x > R.x*.3 && u.x < R.x*.31) 
       { O.b = 1.; return; }                                     // source

    if ( T(0,0).rb == vec2(0) )                                  // if not rock pixel
        O.b = O.a == 0.
            ? T(0,1).b                                           // free air: fall
            : float( 0. < ( T(-1,0)+T(1,0)+T(0,1)+T(0,-1) ).b)   // holes: diffuse
              * (1.+.2*sin(20./R.y*float(iFrame))+.1*sin(float(iFrame)));
}
