void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //clamp (value to be clamped, lower bound, uppper bound)

	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

   /////////////////////////// LICHEN
    vec4 lichen = texture(
        iChannel0, 
        //Sample from linear X and Y based on  time, normalized
        uv+vec2(.05*(iTime), .05*(iTime) )
    	); 

   ////////////////////////// WHITE NOISE 
    vec4 noise = texture(
        //noise image
        iChannel1, 
        //UVs altered based on time, diagonal movement
        uv+ vec2(.04*(iTime), .04*(iTime)), 
        // WiLd CaRd
        mod(iTime/10.0, 1.0) 
    	);

    //////////////////////////PEBBLES
    vec4 noise2 = texture(
        //noise image
    	iChannel2,
        //UVs altered based on time, diagonal movement
        uv +vec2(-.022*(iTime), -.022*(iTime))
    	);  

    //Fade in and out.

    //track a "bouncing" timer based on time
    float time = cos(iTime/2.5);
    //tracker shall always remain > 0
    if(time < 0.0) time*= -1.0;
    //increase brightness of noise2 based on time var
    noise2 *= 2.0 *  clamp( time, 0.2, 1.0) ;


    fragColor = lichen*noise*noise2; 
}
