vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float turns = 4.0 ;
    float speed = -0.333;
    float width = 0.666;


    float colorSpeed 		= -0.25;
    float colorSaturation 	= 0.4; 	// saturation
    float colorValue 		= 0.8; 	// brightness

	vec2 uv = ( fragCoord - .5*iResolution.xy )/ iResolution.y;

    float angle = atan(uv.y, uv.x) / (6.283);
    angle = fract(angle + (iTime * speed));


    float dist = length(uv);
    float waves = fract( dist  * turns) ;

    float spiral = angle - waves;
    //float spiral = (abs(angle - waves) - 0.5 )* 2.0  ;
    //spiral = step((spiral*spiral), 0.1);

    float spiralX = 1.0 - ((spiral - (0.5 * sign(spiral)) ) + 0.5);

    //spiral = abs((abs(spiral) - 0.5 )* 2.0  );

    spiral = step(spiralX, width);

    float gradSpiral = spiral * clamp(dist * (turns / 4.0), 0.0,1.0);

    gradSpiral -= (spiralX * 0.25);
   	vec3 rainbow = hsv2rgb(vec3(gradSpiral - (iTime * colorSpeed), colorSaturation, colorValue)) ;
  	fragColor = vec4( rainbow * spiral , 1.0);


}




