void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p1 = vec2(0.8,0.5);
    vec2 p2 = vec2(0.5)+vec2(sin(iTime)*0.3,cos(iTime)*0.3);
    vec2 p3 = iMouse.xy/iResolution.xy;
    float dist = 
                (sqrt(pow( uv.x-p1.x , 2.0 ) + pow( uv.y - p1.y , 2.0 )))
        		*
        		(sqrt(pow( uv.x-p2.x , 2.0 ) + pow( uv.y - p2.y , 2.0 )))
        *
        		(sqrt(pow( uv.x-p3.x , 2.0 ) + pow( uv.y - p3.y , 2.0 )));
    float intensity = 1.0-pow(dist*40.0,5.0);
    float intensity1 = (sqrt(pow( uv.x-p1.x , 2.0 ) + pow( uv.y - p1.y , 2.0 )));
    float intensity2 = (sqrt(pow( uv.x-p2.x , 2.0 ) + pow( uv.y - p2.y , 2.0 )));
    float intensity3 = (sqrt(pow( uv.x-p3.x , 2.0 ) + pow( uv.y - p3.y , 2.0 )));
	fragColor = vec4(intensity1*intensity,intensity2*intensity,intensity3*intensity,1.0);
}
