float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = texture( iChannel0, (uv+0.5)/256.0, -100.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}



float fcircle(vec2 p) { return 1. - dot(p-=.5,p); }

float fstars(vec2 p) {
    float sz = 1000.0;
 	float n = noise(vec3(p * sz, .2));
    return max(0.0, n - 0.92) * noise(vec3(p * 100., 0)) * 5.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x -= 0.225;
    uv.x *= iResolution.x/iResolution.y;

    float m = iTime;

    float globe = pow(fcircle(uv), 4.0);
    float crown = pow(fcircle(uv), 32.0);
    float sun = globe * 2.0 + crown * 1.5;
    float stars = fstars(uv) * (2.0 - sun);

    float maculas = noise(vec3(uv.x * 4.0 + m * 0.35, uv.y * 4.0 + m * 0.1, 0.0)) * pow(globe, 6.0) * 3.0;


    float r = sun * 1.3 + stars- maculas;
    float g = sun + stars- maculas;
    float b = sun * pow(crown, 0.03) - maculas + stars;

	fragColor = vec4(r, g, b, 1.0);
}
