vec2 R;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
void mainImage( out vec4 C, in vec2 U )
{
    R = iResolution.xy;
    C = T(U);
    C = 0.5+0.5*sin(10.*C.z+3.*C.w*vec4(1.1,1.5,1.8,1));
}

vec2 R;float N;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
float X (vec2 U1, vec2 U, inout vec4 Q, in vec2 r) {
    vec2 V1 = U1 + r, V  = V1 - T(V1).xy;
    vec4 W = T (V);float rr = length(r);
    Q.xy -= r*(W.z-Q.z)/rr/N;
    Q.w = mix(Q.w,W.w,0.01);
    return (length(V-U)-rr+W.z)/N;
}

void mainImage( out vec4 Q, in vec2 U )
{   R = iResolution.xy;
 	vec2 U1 = U; U -= T(U).xy;
 	float P = 0.; Q = T(U);
 	N = 4.;
 	P += X (U1,U,Q, vec2( 1, 0) );
 	P += X (U1,U,Q, vec2( 0,-1) );
 	P += X (U1,U,Q, vec2(-1, 0) );
 	P += X (U1,U,Q, vec2( 0, 1) );
 	Q.z = P;
 	Q.y += 0.001*(Q.w-.5);
 	Q.xy *= 0.999;
    Q.w += 0.01*(floor(Q.w+0.5)-Q.w);
 	if (iFrame < 60) Q = vec4(0,0,0,U.y+2.*sin(0.01*U.x)<0.5*R.y);
    //if (U.x < 1.||U.y < 1.||R.x-U.x < 1.||R.y-U.y < 1.) Q.xy *= 0.;


}

