const int dist = 8;
const float stdev = 6.;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=vec4(0.0,0.0,0.0,1.0);
	vec2 center;
    float correction = 0.;
    if(abs(fragCoord.x-iResolution.x/2.)<256.)
    {
		if(abs(fragCoord.y-iResolution.y/2.)<256.)
    	{
            center = (fragCoord-vec2(256.,256.)-iResolution.xy/2.)/512.;
            if(distance(fragCoord.xy,iResolution.xy/2.+vec2(sin(iTime*2.)*100.,0.))>160.)
            {
                fragColor = texture(iChannel0,center);
                correction = 1.;
            }
            else
            {
            vec2 diffX = vec2(1./512.,0.);
            vec2 diffY = vec2(0.,1./512.);
            for(int x=-dist;x<=dist;x++)
    		{
        		for(int y=-dist;y<=dist;y++)
    			{
                    float multiplier = exp(float(x*x+y*y)/-2./stdev/stdev);
                    correction = correction + multiplier;
					fragColor.xyz = fragColor.xyz +texture(iChannel0, center+float(x)*diffX+float(y)*diffY).xyz*multiplier;
    			}
    		}
            }
    	}
    }
    fragColor = fragColor/correction;
}
