/*
using https://www.shadertoy.com/view/ldl3W8
but raymarching the cell centers
*/

#define ANIMATE

void pR(inout vec2 p, float a)
{
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec2 hash2( vec2 p )
{
	return texture( iChannel0, (p+0.5)/256.0, -100.0 ).xy;
}

vec3 voronoi( in vec2 x )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

	vec2 mg, mr;

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
        }
    }

    return vec3( md, mr );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xx;

    float td = 0.1;
    vec3 is, o = vec3(0.0, 2.0, 0.0), d = vec3(0.0, 0.0, 1.0);
    pR(d.xz, p.x * 0.3);
    pR(d.yz, p.y * 0.3 - 0.2);

    for(int i = 0 ; i < 100 ; ++i)
    {
        is = o + d * td;
    	vec3 c = voronoi(is.xz);
		float dd = length( vec3(c.y, is.y, c.z) ) - 0.1;
        td += dd;   
        if(dd < 0.0 || td > 40.0)
            break;
    }

    float fog = min(1.0, td / 40.0);

	fragColor = vec4(fog,fog,fog,1.0);
}
